import{_ as e,c as o,o as c,a as d}from"./app.ae2cfb91.js";const u=JSON.parse('{"title":"\u3010offer \u6536\u5272\u8BA1\u5212\u3011\u4F60\u80FD\u8BF4\u8BF4\u4F7F\u7528 hooks \u7684\u597D\u5904\u5417\uFF1F","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u{1F4A1} \u77E5\u8BC6\u70B9\u62A2\u5148\u770B","slug":"\u{1F4A1}-\u77E5\u8BC6\u70B9\u62A2\u5148\u770B"},{"level":2,"title":"\u4E00\u3001\u80FD\u8BF4\u8BF4 link \u548C @import \u4E24\u79CD\u65B9\u5F0F\u5F15\u5165 CSS \u7684\u533A\u522B\u5417","slug":"\u4E00\u3001\u80FD\u8BF4\u8BF4-link-\u548C-import-\u4E24\u79CD\u65B9\u5F0F\u5F15\u5165-css-\u7684\u533A\u522B\u5417"},{"level":2,"title":"\u4E8C\u3001\u5B9E\u73B0\u6570\u7EC4\u53BB\u91CD\u4F60\u6709\u591A\u5C11\u79CD\u65B9\u6CD5","slug":"\u4E8C\u3001\u5B9E\u73B0\u6570\u7EC4\u53BB\u91CD\u4F60\u6709\u591A\u5C11\u79CD\u65B9\u6CD5"},{"level":2,"title":"\u4E09\u3001\u8BF4\u8BF4\u4F60\u5BF9\u95ED\u5305\u7684\u7406\u89E3","slug":"\u4E09\u3001\u8BF4\u8BF4\u4F60\u5BF9\u95ED\u5305\u7684\u7406\u89E3"},{"level":2,"title":"\u56DB\u3001\u89E3\u51B3\u8DE8\u57DF\u95EE\u9898\u4F60\u80FD\u60F3\u5230\u51E0\u79CD\u65B9\u6CD5\uFF1F","slug":"\u56DB\u3001\u89E3\u51B3\u8DE8\u57DF\u95EE\u9898\u4F60\u80FD\u60F3\u5230\u51E0\u79CD\u65B9\u6CD5\uFF1F"},{"level":3,"title":"1. Jsonp \u89E3\u51B3\u8DE8\u57DF","slug":"_1-jsonp-\u89E3\u51B3\u8DE8\u57DF"},{"level":3,"title":"2. \u5B9E\u73B0 CORS","slug":"_2-\u5B9E\u73B0-cors"},{"level":3,"title":"3. Nginx \u53CD\u5411\u4EE3\u7406","slug":"_3-nginx-\u53CD\u5411\u4EE3\u7406"},{"level":3,"title":"4. \u6D4F\u89C8\u5668\u5F00\u542F\u8DE8\u57DF","slug":"_4-\u6D4F\u89C8\u5668\u5F00\u542F\u8DE8\u57DF"},{"level":2,"title":"\u4E94\u3001\u4F60\u80FD\u8BF4\u8BF4\u4F7F\u7528 Hooks \u6709\u4EC0\u4E48\u597D\u5904\u5417\uFF1F","slug":"\u4E94\u3001\u4F60\u80FD\u8BF4\u8BF4\u4F7F\u7528-hooks-\u6709\u4EC0\u4E48\u597D\u5904\u5417\uFF1F"},{"level":2,"title":"\u{1F4D6} \u603B\u7ED3","slug":"\u{1F4D6}-\u603B\u7ED3"}],"relativePath":"pages/interview/heading/best1.md"}'),i={name:"pages/interview/heading/best1.md"},a=d('<h1 id="\u3010offer-\u6536\u5272\u8BA1\u5212\u3011\u4F60\u80FD\u8BF4\u8BF4\u4F7F\u7528-hooks-\u7684\u597D\u5904\u5417\uFF1F" tabindex="-1">\u3010offer \u6536\u5272\u8BA1\u5212\u3011\u4F60\u80FD\u8BF4\u8BF4\u4F7F\u7528 hooks \u7684\u597D\u5904\u5417\uFF1F <a class="header-anchor" href="#\u3010offer-\u6536\u5272\u8BA1\u5212\u3011\u4F60\u80FD\u8BF4\u8BF4\u4F7F\u7528-hooks-\u7684\u597D\u5904\u5417\uFF1F" aria-hidden="true">#</a></h1><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/offer%E6%94%B6%E5%89%B2%E8%AE%A1%E5%88%92-%E7%AC%AC%E4%B8%89%E5%BC%B9.png" alt="offer\u6536\u5272\u8BA1\u5212-\u7B2C\u4E09\u5F39" data-fancybox="gallery"></p><blockquote><p>\u{1F4E2} \u5927\u5BB6\u597D\uFF0C\u6211\u662F\u5C0F\u4E1E\u540C\u5B66\uFF0C\u4E00\u540D<strong>\u5927\u4E8C\u7684\u524D\u7AEF\u7231\u597D\u8005</strong></p><p>\u{1F4E2} \u8FD9\u7BC7\u6587\u7AE0\u5C06\u6765\u8BB2\u8BB2\u51E0\u9053\u5E38\u89C1\u7684\u9762\u8BD5\u9898</p><p>\u{1F4E2} \u975E\u5E38\u611F\u8C22\u4F60\u7684\u9605\u8BFB\uFF0C\u4E0D\u5BF9\u7684\u5730\u65B9\u6B22\u8FCE\u6307\u6B63 \u{1F64F}</p><p>\u{1F4E2} <strong>\u613F\u4F60\u5FE0\u4E8E\u81EA\u5DF1\uFF0C\u70ED\u7231\u751F\u6D3B</strong></p></blockquote><h2 id="\u{1F4A1}-\u77E5\u8BC6\u70B9\u62A2\u5148\u770B" tabindex="-1">\u{1F4A1} \u77E5\u8BC6\u70B9\u62A2\u5148\u770B <a class="header-anchor" href="#\u{1F4A1}-\u77E5\u8BC6\u70B9\u62A2\u5148\u770B" aria-hidden="true">#</a></h2><ol><li><code>link</code> \u4E0E <code>@import</code> \u7684\u533A\u522B</li><li>\u591A\u79CD\u65B9\u5F0F\u5B9E\u73B0\u6570\u7EC4\u53BB\u91CD</li><li>\u8BF4\u8BF4\u4F60\u5BF9\u95ED\u5305\u7684\u4E86\u89E3</li><li>JavaScript \u4E2D\u89E3\u51B3\u8DE8\u57DF\u95EE\u9898\u7684\u65B9\u6CD5</li><li>Hooks \u597D\u5904</li></ol><h2 id="\u4E00\u3001\u80FD\u8BF4\u8BF4-link-\u548C-import-\u4E24\u79CD\u65B9\u5F0F\u5F15\u5165-css-\u7684\u533A\u522B\u5417" tabindex="-1">\u4E00\u3001\u80FD\u8BF4\u8BF4 link \u548C @import \u4E24\u79CD\u65B9\u5F0F\u5F15\u5165 CSS \u7684\u533A\u522B\u5417 <a class="header-anchor" href="#\u4E00\u3001\u80FD\u8BF4\u8BF4-link-\u548C-import-\u4E24\u79CD\u65B9\u5F0F\u5F15\u5165-css-\u7684\u533A\u522B\u5417" aria-hidden="true">#</a></h2><p>\u5728\u6211\u4EEC\u521A\u5F00\u59CB\u5B66\u4E60\u524D\u7AEF\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u90FD\u662F\u901A\u8FC7 <code>link</code> \u6765\u5F15\u5165 <code>CSS</code> \u6587\u4EF6\uFF0C\u4F46\u662F\u540E\u6765\u53D1\u73B0\u6211\u4EEC\u4E5F\u53EF\u4EE5\u901A\u8FC7 <code>@import</code> \u53BB\u5F15\u5165</p><p>\u9996\u5148\u5B83\u4EEC\u5728\u7528\u6CD5\u4E0A\u5B58\u5728\u7740\u533A\u522B</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211128212500226.png" alt="image-20211128212500226" data-fancybox="gallery"></p><p>\u4ECE\u5B83\u4EEC\u7684\u7528\u6CD5\u4E0A\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5F88\u6E05\u6670\u7684\u770B\u51FA\u5B83\u4EEC\u4E4B\u95F4\u7684\u533A\u522B</p><ol><li><code>link</code> \u662F <code>html</code> \u6807\u7B7E\uFF0C\u5B83\u4E0D\u4EC5\u53EF\u4EE5\u52A0\u8F7D <code>CSS</code> \u6587\u4EF6\uFF0C\u8FD8\u53EF\u4EE5\u5B9A\u4E49 <code>rel</code> \u8FDE\u63A5\u5C5E\u6027\u7B49\uFF0C\u800C <code>@import</code> \u662F <code>CSS</code> \u7684\u8BED\u6CD5\uFF0C\u53EA\u6709\u5BFC\u5165\u6837\u5F0F\u8868\u7684\u4F5C\u7528</li><li>\u6D4F\u89C8\u5668\u5728\u52A0\u8F7D\u9875\u9762\u65F6\uFF0C\u540C\u6B65\u52A0\u8F7D <code>link</code> \u5F15\u5165\u7684 <code>CSS</code> \u6587\u4EF6\uFF0C<strong>\u9875\u9762\u52A0\u8F7D\u5B8C\u6210\u540E</strong>\uFF0C\u518D\u52A0\u8F7D <code>@import</code> \u5F15\u5165\u7684 <code>CSS</code> \u6837\u5F0F</li><li><code>@import</code> \u53EA\u6709 <code>IE5</code> \u4EE5\u4E0A\u624D\u80FD\u4F7F\u7528\uFF0C\u800C <code>link</code> \u7684\u517C\u5BB9\u6027\u66F4\u597D</li><li><code>JavaScript</code> \u53EA\u80FD\u63A7\u5236 <code>dom</code> \u6539\u53D8 <code>link</code> \u7684\u5F15\u5165\u7684\u6837\u5F0F\uFF0C\u800C\u4E0D\u80FD\u63A7\u5236 <code>@import</code> \u7684\u6837\u5F0F</li><li><code>link</code> \u7684\u6837\u5F0F\u6743\u91CD\u9AD8\u4E8E <code>@import</code> \u7684\u6743\u91CD</li></ol><h2 id="\u4E8C\u3001\u5B9E\u73B0\u6570\u7EC4\u53BB\u91CD\u4F60\u6709\u591A\u5C11\u79CD\u65B9\u6CD5" tabindex="-1">\u4E8C\u3001\u5B9E\u73B0\u6570\u7EC4\u53BB\u91CD\u4F60\u6709\u591A\u5C11\u79CD\u65B9\u6CD5 <a class="header-anchor" href="#\u4E8C\u3001\u5B9E\u73B0\u6570\u7EC4\u53BB\u91CD\u4F60\u6709\u591A\u5C11\u79CD\u65B9\u6CD5" aria-hidden="true">#</a></h2><p>\u6570\u7EC4\u53BB\u91CD\u5728\u524D\u7AEF\u7684\u5E94\u7528\u4E2D\u8FD8\u662F\u5F88\u591A\u7684\uFF0C\u867D\u7136\u6211\u73B0\u5728\u4E00\u65F6\u534A\u4F1A\u60F3\u4E0D\u8D77\u6765\uFF0C\u4F46\u662F\u771F\u7684\u5F88\u91CD\u8981</p><p>\u5728\u8FD9\u91CC\u5C06\u4ECB\u7ECD\u51E0\u79CD\u7B80\u5355\u4E00\u70B9\u7684\u53BB\u91CD\u65B9\u6CD5\uFF0C\u53CC\u5FAA\u73AF\u68C0\u9A8C\u8FD9\u79CD\u5C31\u4E0D\u4ECB\u7ECD\u4E86</p><p><strong>\u7B2C\u4E00\u79CD\uFF1AindexOf \u53BB\u91CD</strong></p><p>\u8FD9\u4E2A\u65B9\u6CD5\u7684\u6838\u5FC3\u5728\u4E8E\uFF1A\u5229\u7528 <code>indexOf</code> \u6765\u5224\u65AD\u8FD9\u4E2A\u5143\u7D20\u5728\u6570\u7EC4\u4E2D\u9996\u6B21\u51FA\u73B0\u7684\u672A\u77E5\u4E0B\u6807\u548C\u5F53\u524D\u7684\u4E0B\u6807\u662F\u5426\u76F8\u7B49\uFF0C\u4E0D\u76F8\u7B49\u5219\u8BF4\u660E\u6CA1\u6709\u6709\u91CD\u590D\u7684\uFF0C\u5220\u9664\u6389</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211128214735820.png" alt="image-20211128214735820" data-fancybox="gallery"></p><p><strong>\u7B2C\u4E8C\u79CD\uFF1Afilter</strong></p><p>\u5229\u7528 <code>filter</code> \u6765\u8FC7\u6EE4\u6570\u7EC4</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211128215521939.png" alt="image-20211128215521939" data-fancybox="gallery"></p><p><strong>\u7B2C\u4E09\u79CD\uFF1ASet</strong></p><p>\u5229\u7528 <code>Set</code> \u96C6\u5408\u7684\u72EC\u4E00\u7279\u6027\u6765\u53BB\u91CD\uFF0C\u8FD9\u4E2A\u65B9\u6CD5\u5C31\u4E66\u5199\u8D77\u6765\u6700\u65B9\u4FBF\u7684</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211128215726707.png" alt="image-20211128215726707" data-fancybox="gallery"></p><p>\u53BB\u91CD\u7684\u65B9\u6CD5\u6570\u4E0D\u80DC\u6570\uFF0C\u5176\u4ED6\u7684\u5C31\u4E0D\u4ECB\u7ECD\u4E86\uFF0C\u5E73\u65F6\u6211\u7528\u7684\u591A\u7684\u662F <code>Set</code> \u53BB\u91CD</p><h2 id="\u4E09\u3001\u8BF4\u8BF4\u4F60\u5BF9\u95ED\u5305\u7684\u7406\u89E3" tabindex="-1">\u4E09\u3001\u8BF4\u8BF4\u4F60\u5BF9\u95ED\u5305\u7684\u7406\u89E3 <a class="header-anchor" href="#\u4E09\u3001\u8BF4\u8BF4\u4F60\u5BF9\u95ED\u5305\u7684\u7406\u89E3" aria-hidden="true">#</a></h2><p>\u5BF9\u4E8E\u95ED\u5305\u8FD9\u4E2A\u6982\u5FF5\uFF0C\u6BCF\u4E2A\u4EBA\u90FD\u4F1A\u6709\u4E0D\u4E00\u6837\u7684\u7406\u89E3\uFF0C\u6211\u4EEC\u5148\u770B\u770B\u51E0\u672C\u4E66\u4E0A\u7684\u89E3\u91CA</p><blockquote><p>\u95ED\u5305\u662F\u6307\u6709\u6743\u8BBF\u95EE\u53E6\u4E00\u4E2A\u51FD\u6570\u4F5C\u7528\u57DF\u4E2D\u7684\u53D8\u91CF\u7684\u51FD\u6570\uFF1B</p></blockquote><blockquote><p>\u4ECE\u6280\u672F\u7684\u89D2\u5EA6\u8BB2\uFF0C\u6240\u6709\u7684JavaScript\u51FD\u6570\u90FD\u662F\u95ED\u5305\uFF1A\u5B83\u4EEC\u90FD\u662F\u5BF9\u8C61\uFF0C\u5B83\u4EEC\u90FD\u5173\u8054\u5230\u4F5C\u7528\u57DF\u94FE\u3002</p></blockquote><blockquote><p>\u5F53\u51FD\u6570\u53EF\u4EE5\u8BB0\u4F4F\u5E76\u8BBF\u95EE\u6240\u5728\u7684\u8BCD\u6CD5\u4F5C\u7528\u57DF\u65F6\uFF0C\u5C31\u4EA7\u751F\u4E86\u95ED\u5305\uFF0C\u5373\u4F7F\u51FD\u6570\u662F\u5728\u5F53\u524D\u8BCD\u6CD5\u4F5C\u7528\u57DF\u4E4B\u5916\u6267\u884C\u3002</p></blockquote><p>\u4EE5\u4E0A\u662F\u4E09\u672C\u524D\u7AEF\u7ECF\u5178\u4E66\u7C4D\u7684\u63CF\u8FF0\uFF0C\u4ECE\u4E0A\u9762\u7684\u63CF\u8FF0\u6765\u770B\uFF0C\u6211\u4EEC\u603B\u7ED3\u4E0D\u51FA\u5565\u6765\u3002</p><p>\u8C08\u8C08\u6211\u7684\u7406\u89E3\u5427</p><p>\u4E00\u4E2A\u51FD\u6570\uFF0C\u80FD\u591F\u8BB0\u4F4F\u5E76\u8BBF\u95EE\u5B83\u8303\u56F4\u5916\u7684\u53D8\u91CF\uFF0C\u8FD9\u4E2A\u51FD\u6570\u548C\u8FD9\u4E2A\u53D8\u91CF\u5C31\u5F62\u6210\u95ED\u5305\uFF0C\u7528\u4E2A\u4F8B\u5B50\u6765\u8BF4\u7684\u8BDD\u5C31\u662F\uFF1A\u5728\u51FD\u6570 A \u5185\u5B9A\u4E49\u4E00\u4E2A\u53D8\u91CF B\uFF0C\u60F3\u5728\u51FD\u6570 A \u5916\u53BB\u8BBF\u95EE\uFF0C\u8FD9\u65F6\u5019\u5728\u51FD\u6570 A \u5185\u5B9A\u4E49\u4E00\u4E2A \u51FD\u6570 C \uFF0C\u628A B \u4F5C\u4E3A\u51FD\u6570 C \u7684\u8FD4\u56DE\u503C\uFF0C\u7136\u540E\u5728\u51FD\u6570 A \u4E2D\u8FD4\u56DE\u51FD\u6570 C</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211128221900875.png" alt="image-20211128221900875" data-fancybox="gallery"></p><h2 id="\u56DB\u3001\u89E3\u51B3\u8DE8\u57DF\u95EE\u9898\u4F60\u80FD\u60F3\u5230\u51E0\u79CD\u65B9\u6CD5\uFF1F" tabindex="-1">\u56DB\u3001\u89E3\u51B3\u8DE8\u57DF\u95EE\u9898\u4F60\u80FD\u60F3\u5230\u51E0\u79CD\u65B9\u6CD5\uFF1F <a class="header-anchor" href="#\u56DB\u3001\u89E3\u51B3\u8DE8\u57DF\u95EE\u9898\u4F60\u80FD\u60F3\u5230\u51E0\u79CD\u65B9\u6CD5\uFF1F" aria-hidden="true">#</a></h2><p>\u5728\u524D\u540E\u7AEF\u4EA4\u4E92\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u7ECF\u5E38\u4F1A\u9047\u5230\u8DE8\u57DF\u7684\u95EE\u9898\uFF0C\u5728\u6211\u4EEC\u4F7F\u7528 <code>React</code> \u3001<code>Vue</code> \u8FD9\u4E9B\u811A\u624B\u67B6\u6765\u642D\u5EFA\u9879\u76EE\u65F6\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5F88\u65B9\u4FBF\u7684\u901A\u8FC7\u914D\u7F6E\u4EE3\u7406\u6765\u89E3\u51B3\u8DE8\u57DF\u7684\u95EE\u9898\uFF0C\u4F46\u662F\u5728\u6211\u4EEC\u521D\u5B66\u524D\u7AEF\u65F6\uFF0C\u8FD9\u4E2A\u95EE\u9898\u771F\u7684\u662F\u975E\u5E38\u68D8\u624B\u7684\uFF0C\u5C31\u6211\u6765\u8BF4\uFF0C\u6211\u8FD8\u6CA1\u6709\u4F7F\u7528\u8FC7\u539F\u751F <code>JS</code> \u6765\u89E3\u51B3\u8FC7\u8DE8\u57DF\u7684\u95EE\u9898\uFF0C\u6211\u60F3\u5F88\u591A\u4EBA\u90FD\u662F\u8FD9\u6837\u7684\uFF0C\u63A5\u4E0B\u6765\u6765\u4E86\u89E3\u4E00\u4E0B\u5982\u4F55\u6765\u89E3\u51B3\u8DE8\u57DF\u95EE\u9898</p><p>\u9996\u5148\u8DE8\u57DF\u662F\u7531\u4E8E\u6D4F\u89C8\u5668\u7684\u540C\u6E90\u7B56\u7565\u5F15\u8D77\u7684\uFF0C\u5B83\u662F\u6D4F\u89C8\u5668\u7684\u5B89\u5168\u529F\u80FD\uFF0C\u4E3A\u4E86\u9632\u6B62 <code>XSS</code>\u3001<code>CSRF</code> \u653B\u51FB\u3002\u6240\u8C13\u7684\u540C\u6E90\u6307\u7684\u662F <strong>\u534F\u8BAE\u3001\u57DF\u540D\u3001\u7AEF\u53E3</strong>\u4E09\u8005\u7684\u76F8\u540C\uFF0C\u5373\u4F7F<strong>\u4E24\u4E2A\u57DF\u540D\u6307\u5411\u540C\u4E00\u53F0\u670D\u52A1\u5668</strong>\u8FD9\u4E5F\u5C5E\u4E8E\u975E\u540C\u6E90</p><p>\u9996\u5148\u6211\u4EEC\u8BB2\u8BB2</p><h3 id="_1-jsonp-\u89E3\u51B3\u8DE8\u57DF" tabindex="-1">1. <code>Jsonp</code> \u89E3\u51B3\u8DE8\u57DF <a class="header-anchor" href="#_1-jsonp-\u89E3\u51B3\u8DE8\u57DF" aria-hidden="true">#</a></h3><p>\u8FD9\u5176\u5B9E\u5229\u7528\u7684\u662F <code>HTML</code> \u6807\u7B7E\u7684\u4E00\u4E2A\u6F0F\u6D1E\u6765\u89E3\u51B3\u7684</p><p>\u6709\u4E09\u4E2A\u6807\u7B7E\u662F\u5141\u8BB8\u8DE8\u57DF\u52A0\u8F7D\u8D44\u6E90\u7684 <code>Img</code> \u3001<code>link</code> \u3001<code>script</code> \u6807\u7B7E</p><p><code>Jsonp</code> \u5C31\u662F\u5229\u7528 <code>script</code> \u6807\u7B7E\u6765\u8DE8\u57DF\u52A0\u8F7D\u8D44\u6E90\uFF0C\u4F46\u662F\u8FD9\u79CD\u65B9\u6CD5\u9700\u8981\u540E\u7AEF\u670D\u52A1\u5668\u7684\u914D\u5408\u624D\u80FD\u5B9E\u73B0\uFF0C\u540C\u65F6\u53EA\u652F\u6301 <code>Get</code> \u8BF7\u6C42</p><ol><li>\u9996\u5148\u6211\u4EEC\u5728\u524D\u7AEF\u4E2D\u9700\u8981\u58F0\u660E\u4E00\u4E2A\u56DE\u8C03\u51FD\u6570</li><li>\u901A\u8FC7 <code>params</code> \u4F20\u53C2\u7684\u65B9\u5F0F\uFF0C\u5C06\u6211\u4EEC\u7684\u8BF7\u6C42\u53C2\u6570\u62FC\u63A5\u5728 <code>url</code> \u8DEF\u5F84\u4E0A</li><li>\u5C06 <code>url</code> \u52A0\u5230 <code>script</code> \u6807\u7B7E\u7684 <code>src</code> \u5C5E\u6027\u4E0A</li><li>\u670D\u52A1\u5668\u4E5F\u9700\u8981\u4E13\u95E8\u9488\u5BF9\u4E8E\u8FD9\u79CD\u8BF7\u6C42\u65B9\u5F0F\u8FDB\u884C\u63A5\u6536\u548C\u7279\u6B8A\u7684\u5904\u7406\uFF0C\u624D\u80FD\u5F97\u5230\u7ED3\u679C</li></ol><p>\u56E0\u6B64\u5982\u679C\u7EAF\u524D\u7AEF\u7684\u8BDD\uFF0C\u662F\u65E0\u6CD5\u901A\u8FC7 <code>Jsonp</code> \u6765\u89E3\u51B3\u8DE8\u57DF\u95EE\u9898\u7684</p><h3 id="_2-\u5B9E\u73B0-cors" tabindex="-1">2. \u5B9E\u73B0 CORS <a class="header-anchor" href="#_2-\u5B9E\u73B0-cors" aria-hidden="true">#</a></h3><p>\u8FD9\u79CD\u65B9\u6CD5\u662F\u6700\u7B80\u5355\u7684\uFF0C\u53EA\u9700\u8981\u540E\u7AEF\u670D\u52A1\u5668\u4E2D\u914D\u7F6E <code> Access-Control-Allow-Origin</code> \u5C31\u53EF\u4EE5\u5F00\u542F <code>CORS</code> \uFF0C\u53EF\u4EE5\u8BBE\u7F6E\u54EA\u4E9B\u57DF\u540D\u53EF\u4EE5\u8BBF\u95EE\u8D44\u6E90\uFF0C\u8FD9\u6837\u5C31\u6CA1\u6709\u524D\u7AEF\u4EC0\u4E48\u4E8B\u4E86\uFF0C</p><h3 id="_3-nginx-\u53CD\u5411\u4EE3\u7406" tabindex="-1">3. Nginx \u53CD\u5411\u4EE3\u7406 <a class="header-anchor" href="#_3-nginx-\u53CD\u5411\u4EE3\u7406" aria-hidden="true">#</a></h3><p>\u5982\u679C\u662F\u524D\u540E\u7AEF\u5206\u79BB\u7684\u9879\u76EE\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5728\u524D\u7AEF\u670D\u52A1\u5668\u7684 <code>Nginx</code> \u914D\u7F6E\u4E2D\uFF0C\u914D\u7F6E\u4EE3\u7406</p><p>\u524D\u7AEF\u5148\u5728\u914D\u7F6E <code>baseUrl</code> \u65F6\uFF0C\u8BF7\u6C42\u524D\u7AEF\u670D\u52A1\u5668\uFF0C\u5E76\u4E14\u6DFB\u52A0\u4E00\u4E2A\u53EF\u6807\u8BC6\u7684\u540E\u7F00\uFF0C\u4F8B\u5982 <code>/api</code></p><p>\u5728\u524D\u7AEF\u670D\u52A1\u5668\u4E0A\u914D\u7F6E</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211128224442518.png" alt="image-20211128224442518" data-fancybox="gallery"></p><h3 id="_4-\u6D4F\u89C8\u5668\u5F00\u542F\u8DE8\u57DF" tabindex="-1">4. \u6D4F\u89C8\u5668\u5F00\u542F\u8DE8\u57DF <a class="header-anchor" href="#_4-\u6D4F\u89C8\u5668\u5F00\u542F\u8DE8\u57DF" aria-hidden="true">#</a></h3><p>\u8FD9\u79CD\u65B9\u6CD5\u8FD8\u662F\u4E0D\u5EFA\u8BAE\u4E86\uFF0C\u7531\u4E8E\u8DE8\u57DF\u95EE\u9898\u662F\u6D4F\u89C8\u5668\u7684\u7B56\u7565\u5F15\u8D77\u7684\uFF0C\u56E0\u6B64\u6211\u4EEC\u53EF\u4EE5\u60F3\u529E\u6CD5<strong>\u5173\u95ED\u6D4F\u89C8\u5668\u8DE8\u57DF\u7684\u9650\u5236</strong></p><p>\u600E\u4E48\u5173\u95ED\uFF0C\u81EA\u5DF1\u627E\u627E\uFF0C\u5173\u4E86\u96BE\u514D\u4F1A\u51FA\u4EC0\u4E48\u95EE\u9898\uFF0C\u65E5\u540E\u53EF\u80FD\u5FD8\u8BB0\u5F00\u56DE\u6765\u5565\u7684\uFF0C\u4E0D\u5927\u597D</p><hr><p>\u6211\u80FD\u60F3\u5230\u7684\uFF0C\u6709\u4E86\u89E3\u8FC7\u7684\u5C31\u8FD9\u4E9B\u4E86\uFF0C\u8FD8\u6709\u5F88\u591A\u89E3\u51B3\u8DE8\u57DF\u7684\u65B9\u6CD5\uFF0C\u4F46\u662F\u6211\u8FD8\u4E0D\u662F\u5F88\u4E86\u89E3\uFF0C\u5C31\u4E0D\u8BEF\u5BFC\u5927\u5BB6\u4E86</p><h2 id="\u4E94\u3001\u4F60\u80FD\u8BF4\u8BF4\u4F7F\u7528-hooks-\u6709\u4EC0\u4E48\u597D\u5904\u5417\uFF1F" tabindex="-1">\u4E94\u3001\u4F60\u80FD\u8BF4\u8BF4\u4F7F\u7528 Hooks \u6709\u4EC0\u4E48\u597D\u5904\u5417\uFF1F <a class="header-anchor" href="#\u4E94\u3001\u4F60\u80FD\u8BF4\u8BF4\u4F7F\u7528-hooks-\u6709\u4EC0\u4E48\u597D\u5904\u5417\uFF1F" aria-hidden="true">#</a></h2><p>\u9996\u5148\u6700\u660E\u663E\u7684\u597D\u5904\u5C31\u662F\u4EE3\u7801\u5199\u8D77\u6765\u66F4\u52A0\u6D41\u7545\u4E86\uFF0C\u76F8\u6BD4\u4E8E\u7C7B\u5F0F\u7EC4\u4EF6\u6765\u8BF4\uFF0C <code>hooks</code> \u4E0D\u9700\u8981\u4F7F\u7528\u50CF <code>componentWillMount</code> \u7B49\u8FD9\u7C7B\u751F\u547D\u5468\u671F\u94A9\u5B50\uFF0C\u4EE3\u7801\u5199\u8D77\u6765\u66F4\u52A0\u81EA\u7531\u4E86\uFF0C\u6CA1\u6709\u90A3\u4E48\u591A\u7684\u9650\u5236\uFF0C\u901A\u8FC7\u4E00\u4E2A <code>useEffect</code> hook \u5C31\u80FD\u4E00\u6B21\u5B9E\u73B0 3 \u4E2A\u751F\u547D\u5468\u671F\u7684\u529F\u80FD\uFF0C\u8FD9\u5BF9\u5F00\u53D1\u8005\u6765\u8BF4\u5341\u5206\u7684\u53CB\u597D\u3002</p><p>\u518D\u4ECE\u5E94\u7528\u4E0A\u6765\u8BF4\uFF0C\u91C7\u7528 <code>hooks</code> \u4E0D\u518D\u9700\u8981\u65F6\u523B\u5173\u6CE8 <code>this</code> \u6307\u5411\u7684\u95EE\u9898\uFF0C\u5728\u7C7B\u5F0F\u7EC4\u4EF6\u4E2D\uFF0C\u6211\u4EEC\u9700\u8981\u65F6\u65F6\u523B\u523B\u5730\u5173\u6CE8\u7740 <code>this</code> \u7684\u6307\u5411\uFF0C\u751F\u6015\u54EA\u91CC\u6307\u9519\u4E86\u5BFC\u81F4\u51FA\u4E86\u95EE\u9898</p><p><code>hooks</code> \u6700\u5927\u7684\u597D\u5904\u5C31\u662F\u7EC4\u4EF6\u7684\u590D\u7528\uFF0C\u5BF9\u4E8E\u6BCF\u4E00\u4E2A\u4F7F\u7528\u8BE5\u7EC4\u4EF6\u7684\u5730\u65B9\uFF0C\u5C31\u4F1A\u5F00\u8F9F\u4E00\u4E2A\u72EC\u7ACB\u7684\u5185\u5B58\u7A7A\u95F4\uFF0C\u5C31\u50CF\u662F\u5728\u8C03\u7528\u51FD\u6570\u4E00\u6837\uFF0C\u5341\u5206\u7684\u7B80\u5355</p><p>\u4EE3\u7801\u7684\u53EF\u8BFB\u6027\u5F3A\uFF0C\u6211\u4EEC\u4E0D\u9700\u8981\u4E13\u95E8\u4F7F\u7528\u751F\u547D\u5468\u671F\u51FD\u6570\u5C31\u80FD\u5B9E\u73B0\u529F\u80FD\u7684\u805A\u5408\uFF0C\u66F4\u52A0\u65B9\u4FBF\u9605\u8BFB\u548C\u4EE3\u7801\u7684\u7EF4\u62A4</p><p><code>hooks</code> \u5E95\u5C42\u5B9E\u73B0\u5DF2\u7ECF\u5E2E\u52A9\u6211\u4EEC\u4F18\u5316\u4E86\u5F88\u591A\u7684\u4EE3\u7801\uFF0C\u6211\u4EEC\u53EA\u9700\u8981\u5728\u4F7F\u7528 <code>hooks</code> \u65F6\uFF0C\u907F\u514D\u7EC4\u4EF6\u7684\u91CD\u590D\u6E32\u67D3\uFF0C\u9002\u5F53\u63A7\u5236\u6E32\u67D3\u6B21\u6570\u5373\u53EF\u505A\u5230\u5F88\u597D\u7684\u4F18\u5316\uFF0C\u540C\u65F6 <code>hooks</code> \u4E5F\u63D0\u4F9B\u4E86\u7C7B\u4F3C\u4E8E <code>useMemo</code> <code>useCallback</code> \u8FD9\u6837\u7684 <code>hook</code> \u6765\u7ED9\u6211\u4EEC\u505A\u6027\u80FD\u4F18\u5316</p><p>\u56E0\u6B64\u6211\u89C9\u5F97 <code>hooks</code> \u5728\u5199\u6CD5\u4E0A\u3001\u53EF\u8BFB\u6027\u4E0A\u3001\u53EF\u590D\u7528\u6027\u3001\u6027\u80FD\u4E0A\uFF08\u5927\u90E8\u5206\u60C5\u51B5\uFF09\u90FD\u4F18\u4E8E\u7C7B\u5F0F\u7EC4\u4EF6</p><hr><h2 id="\u{1F4D6}-\u603B\u7ED3" tabindex="-1">\u{1F4D6} \u603B\u7ED3 <a class="header-anchor" href="#\u{1F4D6}-\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u901A\u8FC7\u8FD9\u51E0\u9053\u9762\u8BD5\u9898\uFF0C\u81EA\u5DF1\u4E5F\u601D\u8003\u4E86\u5F88\u591A\uFF0C\u5173\u4E8E\u95ED\u5305\u5173\u4E8E hooks \uFF0C\u90FD\u6709\u4E00\u4E2A\u6211\u7684\u8BA4\u8BC6\u548C\u7406\u89E3\uFF0C\u6BCF\u4E2A\u4EBA\u7684\u7406\u89E3\u5404\u6709\u5343\u79CB\uFF0C\u4E5F\u60F3\u542C\u542C\u4F60\u4EEC\u7684\u770B\u6CD5~</p><p>\u6700\u540E\uFF0C\u6211\u662F\u5C0F\u4E1E\u540C\u5B66\uFF0C\u6B22\u8FCE\u5927\u5BB6\u5173\u6CE8\u672C\u4E13\u680F\uFF0C\u6301\u7EED\u5173\u6CE8\u6700\u65B0\u6587\u7AE0~\u795D\u613F\u5927\u5BB6\u62FF\u5230\u5FC3\u4EEA\u7684 <code>offer</code></p><blockquote><p>\u6700\u540E\uFF0C\u53EF\u80FD\u5728\u5F88\u591A\u5730\u65B9\u8BB2\u8BC9\u7684\u4E0D\u591F\u6E05\u6670\uFF0C\u8BF7\u89C1\u8C05</p><p>\u{1F48C} \u5982\u679C\u6587\u7AE0\u6709\u4EC0\u4E48\u9519\u8BEF\u7684\u5730\u65B9\uFF0C\u6216\u8005\u6709\u4EC0\u4E48\u7591\u95EE\uFF0C\u6B22\u8FCE\u7559\u8A00\uFF0C\u4E5F\u6B22\u8FCE\u79C1\u4FE1\u4EA4\u6D41</p></blockquote>',67),l=[a];function t(s,p,r,n,h,g){return c(),o("div",null,l)}var f=e(i,[["render",t]]);export{u as __pageData,f as default};
