import{_ as e,c as o,o as d,a as c}from"./app.434af41f.js";const h=JSON.parse('{"title":"\u3010offer \u6536\u5272\u8BA1\u5212\u3011\u4F60\u77E5\u9053\u4E3A\u4EC0\u4E48 reducer \u6700\u597D\u662F\u4E00\u4E2A\u7EAF\u51FD\u6570\u5417\uFF1F","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u524D\u8A00","slug":"\u524D\u8A00"},{"level":2,"title":"\u{1F4A1} \u77E5\u8BC6\u70B9\u62A2\u5148\u770B","slug":"\u{1F4A1}-\u77E5\u8BC6\u70B9\u62A2\u5148\u770B"},{"level":2,"title":"\u4E00\u3001\u4F60\u80FD\u8BF4\u8BF4 for ... in \u548C for ... of \u7684\u533A\u522B\u5417\uFF1F","slug":"\u4E00\u3001\u4F60\u80FD\u8BF4\u8BF4-for-in-\u548C-for-of-\u7684\u533A\u522B\u5417\uFF1F"},{"level":2,"title":"\u4E8C\u3001\u6765\u8BF4\u8BF4\u6570\u7EC4\u91CC\u7684 slice \u548C splice \u65B9\u6CD5","slug":"\u4E8C\u3001\u6765\u8BF4\u8BF4\u6570\u7EC4\u91CC\u7684-slice-\u548C-splice-\u65B9\u6CD5"},{"level":2,"title":"\u4E09\u3001\u4E3A\u4EC0\u4E48\u6709\u4E86 indexOf \u65B9\u6CD5\uFF0C\u5728 ES7 \u4E2D\u8FD8\u8981\u65B0\u589E includes \u65B9\u6CD5\u5462\uFF1F","slug":"\u4E09\u3001\u4E3A\u4EC0\u4E48\u6709\u4E86-indexof-\u65B9\u6CD5\uFF0C\u5728-es7-\u4E2D\u8FD8\u8981\u65B0\u589E-includes-\u65B9\u6CD5\u5462\uFF1F"},{"level":2,"title":"\u56DB\u3001\u4F2A\u5143\u7D20\u6709\u54EA\u4E9B\u4F5C\u7528\u5462\uFF1F","slug":"\u56DB\u3001\u4F2A\u5143\u7D20\u6709\u54EA\u4E9B\u4F5C\u7528\u5462\uFF1F"},{"level":2,"title":"\u4E94\u3001500 HTTP \u72B6\u6001\u7801\u662F\u4EC0\u4E48\u5F02\u5E38\uFF1F","slug":"\u4E94\u3001500-http-\u72B6\u6001\u7801\u662F\u4EC0\u4E48\u5F02\u5E38\uFF1F"},{"level":2,"title":"\u516D\u3001redux \u4E2D\u7684 reducer \u8981\u6C42\u662F\u4E00\u4E2A\u7EAF\u51FD\u6570\u5462\uFF1F","slug":"\u516D\u3001redux-\u4E2D\u7684-reducer-\u8981\u6C42\u662F\u4E00\u4E2A\u7EAF\u51FD\u6570\u5462\uFF1F"},{"level":2,"title":"\u{1F4D6} \u603B\u7ED3","slug":"\u{1F4D6}-\u603B\u7ED3"}],"relativePath":"pages/interview/heading/best3.md"}'),t={name:"pages/interview/heading/best3.md"},i=c('<h1 id="\u3010offer-\u6536\u5272\u8BA1\u5212\u3011\u4F60\u77E5\u9053\u4E3A\u4EC0\u4E48-reducer-\u6700\u597D\u662F\u4E00\u4E2A\u7EAF\u51FD\u6570\u5417\uFF1F" tabindex="-1">\u3010offer \u6536\u5272\u8BA1\u5212\u3011\u4F60\u77E5\u9053\u4E3A\u4EC0\u4E48 reducer \u6700\u597D\u662F\u4E00\u4E2A\u7EAF\u51FD\u6570\u5417\uFF1F <a class="header-anchor" href="#\u3010offer-\u6536\u5272\u8BA1\u5212\u3011\u4F60\u77E5\u9053\u4E3A\u4EC0\u4E48-reducer-\u6700\u597D\u662F\u4E00\u4E2A\u7EAF\u51FD\u6570\u5417\uFF1F" aria-hidden="true">#</a></h1><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/offer%E6%94%B6%E5%89%B2%E8%AE%A1%E5%88%92-%E7%AC%AC%E4%B8%80%E5%BC%B9.png" alt="offer\u6536\u5272\u8BA1\u5212-\u7B2C\u4E00\u5F39" data-fancybox="gallery"></p><blockquote><p>\u{1F4E2} \u5927\u5BB6\u597D\uFF0C\u6211\u662F\u5C0F\u4E1E\u540C\u5B66\uFF0C\u4E00\u540D<strong>\u5927\u4E8C\u7684\u524D\u7AEF\u7231\u597D\u8005</strong></p><p>\u{1F4E2} \u8FD9\u7BC7\u6587\u7AE0\u5C06\u6765\u8BB2\u8BB2\u6700\u8FD1\u65E0\u610F\u4E2D\u770B\u5230\u7684\u51E0\u9053\u9762\u8BD5\u9898\uFF0C\u4E5F\u5E0C\u671B\u6765\u770B\u770B\u5927\u5BB6\u7684\u7406\u89E3~</p><p>\u{1F4E2} \u975E\u5E38\u611F\u8C22\u4F60\u7684\u9605\u8BFB\uFF0C\u4E0D\u5BF9\u7684\u5730\u65B9\u6B22\u8FCE\u6307\u6B63 \u{1F64F}</p><p>\u{1F4E2} <strong>\u613F\u4F60\u5FE0\u4E8E\u81EA\u5DF1\uFF0C\u70ED\u7231\u751F\u6D3B</strong></p></blockquote><h2 id="\u524D\u8A00" tabindex="-1">\u524D\u8A00 <a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a></h2><p>\u6700\u8FD1\u770B\u4E86\u770B\u4E86\u4E0B\u4E00\u4E9B\u535A\u4E3B\u7684\u9762\u8BD5\u6587\uFF0C\u81EA\u5DF1\u4E5F\u8DDF\u7740\u601D\u8003\u4E86\u4E00\u4E0B\u4E00\u4E9B\u9898\u76EE\uFF0C\u53D1\u73B0\u6709\u5F88\u591A\u7B80\u5355\u800C\u53C8\u91CD\u8981\u7684\u77E5\u8BC6\u70B9\u6709\u70B9\u62FF\u634F\u4E0D\u4F4F\uFF0C\u56E0\u6B64\u51B3\u5B9A\u5199\u4E00\u4E2A\u4E13\u680F\uFF0C\u6765\u8BB0\u5F55\u8FD9\u4E9B\u9898\u76EE\uFF0C\u5E76\u5199\u4E0A\u81EA\u5DF1\u7684\u7406\u89E3\uFF0C\u66F4\u91CD\u8981\u7684\u662F\uFF0C\u5E0C\u671B\u6709\u8BFB\u8005\u80FD\u591F\u5206\u4EAB\u81EA\u5DF1\u7684\u7406\u89E3\uFF0C\u6216\u8005\u5728\u54EA\u4E9B\u9898\u76EE\u9047\u5230\u4E86\u95EE\u9898\uFF0C\u8FD9\u6837\u6211\u4EEC\u53EF\u4EE5\u4E00\u8D77\u5173\u6CE8\u4E00\u4E0B\u8FD9\u4E9B\u9898\u76EE\uFF0C\u5927\u5BB6\u5171\u540C\u8FDB\u6B65\uFF01\u56E0\u6B64\u5E0C\u671B\u5728\u8BFB\u8FD9\u7BC7\u6587\u7AE0\u7684\u4F60\uFF0C\u53EF\u4EE5\u81EA\u5DF1\u5148\u601D\u8003\u4E00\u4E0B\uFF0C\u518D\u770B\u770B\u6211\u7684\u7406\u89E3\uFF0C\u8FD9\u6837\u4E5F\u80FD\u8D77\u5230\u5BF9\u6211\u7684\u6587\u7AE0\u6B63\u786E\u6027\u7684\u68C0\u9A8C\u3002</p><ul><li>\u5982\u679C\u6587\u7AE0\u5185\u5BB9\u6709\u4EC0\u4E48\u9519\u8BEF\u7684\u5730\u65B9\uFF0C\u4E5F\u8BF7\u5927\u5BB6\u52A1\u5FC5\u6307\u51FA\uFF01</li><li>\u5982\u679C\u671F\u5F85\u672C\u4E13\u680F\u7684\u65B0\u6587\u7AE0\uFF0C\u4E5F\u53EF\u4EE5\u8BC4\u8BBA\u7559\u8A00\u5662\uFF01</li><li>\u5982\u679C\u4F60\u662F\u540C\u9F84\u4EBA\uFF0C\u6216\u8005\u6709\u4EC0\u4E48\u9700\u6C42\uFF0C\u53EF\u4EE5\u6DFB\u52A0\u6211\u7684 vx \u548C\u6211\u4E00\u8D77\u63A2\u8BA8\uFF01</li></ul><h2 id="\u{1F4A1}-\u77E5\u8BC6\u70B9\u62A2\u5148\u770B" tabindex="-1">\u{1F4A1} \u77E5\u8BC6\u70B9\u62A2\u5148\u770B <a class="header-anchor" href="#\u{1F4A1}-\u77E5\u8BC6\u70B9\u62A2\u5148\u770B" aria-hidden="true">#</a></h2><ol><li><code>for...in</code> \u548C <code>for ... of</code> \u7684\u533A\u522B</li><li><code>splice</code> \u548C <code>slice</code> \u7684\u533A\u522B</li><li><code>includes </code> \u548C <code>indexOf</code> \u7684\u5DEE\u5F02</li><li>\u4F2A\u7C7B\u7684\u4F5C\u7528</li><li><code>ajax</code> \u72B6\u6001\u7801</li><li><code>redux</code> \u4E2D\u7684 <code>reducer</code> \u4E3A\u4EC0\u4E48\u6700\u597D\u662F\u4E00\u4E2A\u7EAF\u51FD\u6570\uFF1F</li></ol><h2 id="\u4E00\u3001\u4F60\u80FD\u8BF4\u8BF4-for-in-\u548C-for-of-\u7684\u533A\u522B\u5417\uFF1F" tabindex="-1">\u4E00\u3001\u4F60\u80FD\u8BF4\u8BF4 <code>for ... in</code> \u548C <code>for ... of</code> \u7684\u533A\u522B\u5417\uFF1F <a class="header-anchor" href="#\u4E00\u3001\u4F60\u80FD\u8BF4\u8BF4-for-in-\u548C-for-of-\u7684\u533A\u522B\u5417\uFF1F" aria-hidden="true">#</a></h2><p>\u7B80\u5355\u6765\u8BF4 <code>for...in</code> \u548C <code>for ... of</code> \u90FD\u662F\u7528\u6765\u904D\u5386\u7684\uFF0C\u4F46\u662F<code>for ... in</code> \u904D\u5386\u7684\u662F\u6570\u7EC4\u7684\u7D22\u5F15 <strong>index</strong>\uFF0C\u800C <code>for ... of</code> \u904D\u5386\u7684\u662F\u6570\u7EC4\u7684\u5143\u7D20\u503C <strong>value</strong></p><p><strong>\u5148\u8BF4\u8BF4 <code>for ...of</code></strong></p><p>\u2705 \u5B83\u53EA\u80FD\u904D\u5386\u90E8\u7F72\u4E86 <code>iterator</code> \u63A5\u53E3\u7684\u6570\u636E\u7ED3\u6784\uFF0C\u5BF9\u8C61\u5982\u679C\u4E0D\u5B9E\u73B0 <code>iterator</code> \u63A5\u53E3\uFF0C\u4E5F\u662F\u65E0\u6CD5\u4F7F\u7528 <code>for ... of</code> \u904D\u5386\u7684</p><p>\u2705 <code>for ... of</code> \u4E0D\u53EA\u662F\u53EF\u4EE5\u904D\u5386\u5143\u7D20\u7684 <code>value</code> \u503C\uFF0C\u4F60\u4E5F\u53EF\u4EE5\u901A\u8FC7\u4E0B\u9762\u8FD9\u79CD\u65B9\u5F0F\u6765\u904D\u5386\u51FA\u5BF9\u8C61\u7684 <code>key, value</code> \u503C\uFF0C\u4F46\u662F\u8FD9\u6837\u4F1A\u76F8\u5BF9\u7684\u9EBB\u70E6\u4E00\u4E9B\uFF0C\u56E0\u6B64\u4E0D\u63A8\u8350 <code>for ... of</code> \u6765\u904D\u5386\u5BF9\u8C61</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211125142848377.png" alt="image-20211125142848377" data-fancybox="gallery"></p><p>\u2705 <code>for...of</code> \u66F4\u9002\u5408\u904D\u5386\u6570\u7EC4\uFF0C\u5E76\u4E14\u5B83\u53EA\u662F\u904D\u5386\u6570\u7EC4\u5185\u7684\u5143\u7D20\uFF0C\u4E0D\u4F1A\u904D\u5386\u539F\u578B\u5C5E\u6027\u4EE5\u53CA\u81EA\u8EAB\u7684\u5C5E\u6027</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211125143447722.png" alt="image-20211125143447722" data-fancybox="gallery"></p><p><strong>\u518D\u6765\u770B\u770B <code>for ... in</code></strong></p><p><code>for ... in</code> \u66F4\u9002\u5408\u904D\u5386\u5BF9\u8C61\uFF0C\u5728\u7528\u6765\u904D\u5386\u6570\u7EC4\u7684\u65F6\u5019\uFF0C\u7531\u4E8E <code>for ... in</code> \u7684\u7279\u6027\u4F1A\u9020\u6210\u4E00\u4E9B\u95EE\u9898</p><p>\u2705 <code>for ... in</code> \u4F1A\u904D\u5386\u51FA\u6570\u7EC4\u7684\u539F\u578B\u5BF9\u8C61\uFF0C\u4EE5\u53CA\u6570\u7EC4\u81EA\u8EAB\u7684\u5C5E\u6027\uFF0C\u56E0\u6B64\u4E0B\u9762\u7684 <code>index</code> \u4E2D\u4F1A\u6253\u5370\u51FA <code>a</code></p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211125144503702.png" alt="image-20211125144503702" data-fancybox="gallery"></p><p>\u2705 \u540C\u65F6\u503C\u5F97\u6CE8\u610F\u7684\u662F\uFF0C<code>for ... in</code> \u904D\u5386\u51FA\u6765\u7684 <code>index</code> \u5E76\u4E0D\u662F\u4E00\u4E2A <code>number</code> \u7C7B\u578B\uFF0C\u800C\u662F\u4E00\u4E2A <code>string</code> \u7C7B\u578B\uFF0C\u56E0\u6B64\u5728\u4F7F\u7528 <code>index</code> \u6765\u8FDB\u884C\u8BA1\u7B97\u7684\u65F6\u5019\u9700\u8981\u6CE8\u610F</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211125144901645.png" alt="image-20211125144901645" data-fancybox="gallery"></p><p>\u{1F449} \u603B\u7ED3\u4EE5\u4E0A\uFF0C<code>for ... in</code> \u548C <code>for ... of</code> \u7684\u533A\u522B\u6709\u4EE5\u4E0B\u51E0\u70B9</p><ol><li><code>for ... in</code> \u5FAA\u73AF\u51FA\u7684\u662F <code>index</code>\uFF0C<code>for ... of</code> \u5FAA\u73AF\u51FA\u7684\u662F <code>value</code></li><li><code>for ... of</code> <strong>\u4E0D\u80FD</strong>\u5FAA\u73AF\u666E\u901A\u5BF9\u8C61\uFF0C\u9700\u8981\u5B9E\u73B0 <code>iterator</code> \u63A5\u53E3</li><li><code>for ... of</code> <strong>\u4E0D\u4F1A</strong>\u904D\u5386\u539F\u578B\u4EE5\u53CA\u81EA\u8EAB\u7684\u5C5E\u6027\uFF0C\u800C <code>for ... in</code> \u4F1A</li><li><code>for ... of</code> \u662F <code>ES6</code> \u7684\u65B0\u8BED\u6CD5</li></ol><h2 id="\u4E8C\u3001\u6765\u8BF4\u8BF4\u6570\u7EC4\u91CC\u7684-slice-\u548C-splice-\u65B9\u6CD5" tabindex="-1">\u4E8C\u3001\u6765\u8BF4\u8BF4\u6570\u7EC4\u91CC\u7684 <code>slice</code> \u548C <code>splice</code> \u65B9\u6CD5 <a class="header-anchor" href="#\u4E8C\u3001\u6765\u8BF4\u8BF4\u6570\u7EC4\u91CC\u7684-slice-\u548C-splice-\u65B9\u6CD5" aria-hidden="true">#</a></h2><p><code>slice</code> \u65B9\u6CD5\u4E3B\u8981\u662F\u7528\u6765\u622A\u53D6\u6570\u7EC4\u4EE5\u53CA\u5B57\u7B26\u4E32\uFF0C\u5B83\u63A5\u6536\u4E24\u4E2A\u53C2\u6570\uFF0C\u4E00\u4E2A\u662F\u622A\u53D6\u7684\u8D77\u59CB\u4F4D\u7F6E\uFF0C\u4E00\u4E2A\u662F\u622A\u53D6\u7684\u7ED3\u675F\u4F4D\u7F6E\uFF0C\u540C\u65F6\u5B83\u4F1A\u8FD4\u56DE\u622A\u53D6\u5143\u7D20\u7EC4\u6210\u7684\u65B0\u6570\u7EC4\uFF0C\u5E76\u4E14\u4E0D\u4F1A\u6539\u53D8\u539F\u6570\u7EC4</p><p>\u53EF\u4EE5\u770B\u5230\u4ECE\u7D22\u5F15\u4E3A <code>1</code> \u7684\u5730\u65B9\u622A\u53D6\u5230\u7D22\u5F15\u4E3A <code>3</code> \u7684\u5730\u65B9\u7ED3\u675F\uFF0C\u8FD4\u56DE\u7684\u662F\u4E00\u4E2A\u88AB\u622A\u53D6\u7684\u6570\u7EC4\uFF0C\u540C\u65F6\u539F\u6570\u7EC4\u6CA1\u6709\u88AB\u6539\u53D8</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211125151258884.png" alt="image-20211125151258884" data-fancybox="gallery"></p><p><code>splice</code> \u65B9\u6CD5\u4E3B\u8981\u7528\u6765\u5220\u9664\u6570\u7EC4\uFF0C\u5E76\u4E14\u53EF\u4EE5\u6DFB\u52A0\u6570\u7EC4\u5143\u7D20\uFF0C\u5B83\u63A5\u6536\u7684\u7B2C\u4E00\u4E2A\u53C2\u6570\u662F\u8D77\u59CB\u7684\u7D22\u5F15\uFF0C\u7B2C\u4E8C\u4E2A\u53C2\u6570\u662F\u5220\u9664\u7684\u4E2A\u6570\uFF0C\u540E\u9762\u7684\u53C2\u6570\u90FD\u662F\u9700\u8981\u6DFB\u52A0\u7684\u5143\u7D20</p><p>\u7B2C\u4E8C\u4E2A\u53C2\u6570\u4EE5\u540E\u7684\u53C2\u6570\u662F\u9700\u8981\u589E\u52A0\u7684\u5143\u7D20\uFF0C\u5728\u8D77\u59CB\u4F4D\u7F6E\u63D2\u5165\uFF0C\u53EF\u4EE5\u7406\u89E3\u4E3A\uFF0C\u5220\u9664\u4E86\u4E00\u4E9B\u5143\u7D20\uFF0C\u7136\u540E\u5728\u8FD9\u91CC\u8865\u4E0A\u4E00\u4E9B\u65B0\u7684\u5143\u7D20\uFF0C<code>splice</code> \u4F1A\u6539\u53D8\u539F\u6570\u7EC4</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211125150954385.png" alt="image-20211125150954385" data-fancybox="gallery"></p><p>\u53EF\u4EE5\u770B\u5230\u4ECE\u7D22\u5F15\u4E3A 1 \u7684\u5730\u65B9\u5F00\u59CB\uFF08\u5305\u62EC 1\uFF09\uFF0C\u5F00\u59CB\u5220\u9664 2 \u4E2A\u5143\u7D20\uFF0C\u4E5F\u5C31\u662F <code>b\uFF0Cc</code> \uFF0C\u968F\u540E\u5728\u8FD9\u91CC\u8865\u4E0A\u4E86 2 \u4E2A\u5143\u7D20\uFF0C\u540C\u65F6\u4E5F\u53EF\u4EE5\u770B\u51FA\u539F\u6570\u7EC4 <code>arr</code> \u88AB\u6539\u53D8\u4E86</p><p>\u{1F449} \u603B\u7ED3\u4EE5\u4E0A\uFF1A</p><ol><li>\u4E24\u8005\u5E76\u6CA1\u6709\u4EC0\u4E48\u76F4\u63A5\u7684\u5173\u8054\uFF0C<code>splice</code> \u7528\u6765\u589E\u5220\u6570\u7EC4\uFF0C<code>slice</code> \u7528\u6765\u622A\u53D6\u6570\u7EC4\u6216\u5B57\u7B26\u4E32</li><li><code>splice</code> \u4F1A\u6539\u53D8\u539F\u6570\u7EC4\uFF0C<code>slice</code> \u4E0D\u4F1A\u6539\u53D8\u539F\u6570\u7EC4</li></ol><h2 id="\u4E09\u3001\u4E3A\u4EC0\u4E48\u6709\u4E86-indexof-\u65B9\u6CD5\uFF0C\u5728-es7-\u4E2D\u8FD8\u8981\u65B0\u589E-includes-\u65B9\u6CD5\u5462\uFF1F" tabindex="-1">\u4E09\u3001\u4E3A\u4EC0\u4E48\u6709\u4E86 <code>indexOf</code> \u65B9\u6CD5\uFF0C\u5728 ES7 \u4E2D\u8FD8\u8981\u65B0\u589E <code>includes</code> \u65B9\u6CD5\u5462\uFF1F <a class="header-anchor" href="#\u4E09\u3001\u4E3A\u4EC0\u4E48\u6709\u4E86-indexof-\u65B9\u6CD5\uFF0C\u5728-es7-\u4E2D\u8FD8\u8981\u65B0\u589E-includes-\u65B9\u6CD5\u5462\uFF1F" aria-hidden="true">#</a></h2><p>\u5728\u4E4B\u524D\u7684 <code>indexOf</code> \u65B9\u6CD5\u4E2D\u5B58\u5728\u7740\u4E00\u4E9B\u95EE\u9898\uFF0C\u4E3B\u8981\u662F\u5728\u4E8E <code>NaN</code> \u7684\u5224\u65AD\u4E0A\uFF0C<code>indexOf</code> \u6CA1\u6709\u529E\u6CD5\u53BB\u5224\u65AD\u6570\u7EC4\u4E2D\u662F\u5426\u5B58\u5728 <code>NaN</code> \u503C\uFF0C\u5F53\u6211\u4EEC\u9700\u8981\u5224\u65AD\u6570\u7EC4\u4E2D\u662F\u5426\u5B58\u5728 <code>NaN</code> \u503C\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u9700\u8981\u91C7\u7528 <code>includes</code> \u6765\u5224\u65AD</p><p>\u91C7\u7528 <code>indexOf</code> \u5C31\u4F1A\u51FA\u73B0\u4E0B\u9762\u8FD9\u6837\u7684\u5DEE\u5F02</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211125152557654.png" alt="image-20211125152557654" data-fancybox="gallery"></p><p>\u540C\u65F6\u5F53\u6570\u7EC4\u6709\u7A7A\u503C\u7684\u65F6\u5019\uFF0C <code>includes</code> \u4F1A\u8BA4\u4E3A\u7A7A\u503C\u4E3A <code>undefined</code> \uFF0C\u800C <code>indexOf</code> \u4E0D\u4F1A\uFF0C\u518D\u6765\u770B\u6BB5\u4EE3\u7801</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211125152831294.png" alt="image-20211125152831294" data-fancybox="gallery"></p><p>\u56E0\u6B64\uFF0C<code>includes</code> \u7684\u51FA\u73B0\u5C31\u662F\u4E3A\u4E86\u89E3\u51B3 <code>indexOf</code> \u9057\u7559\u7684\u4E00\u4E9B\u95EE\u9898</p><p>\u{1F449} \u603B\u7ED3\u4EE5\u4E0A\uFF1A</p><ol><li><code>includes</code> \u80FD\u591F\u5224\u65AD\u6570\u7EC4\u4E2D\u6709\u65E0 <code>NaN</code> \u503C</li><li><code>includes</code> \u4F1A\u628A\u7A7A\u503C\u9ED8\u8BA4\u6210 <code>undefined</code></li><li>\u5982\u679C\u60F3\u8981\u5224\u65AD\u6570\u7EC4\u4E2D\u662F\u5426\u5B58\u5728\u67D0\u4E2A\u503C\uFF0C\u53EF\u4EE5\u91C7\u7528 <code>includes</code> \uFF0C\u67E5\u627E\u6570\u7EC4\u4E2D\u67D0\u4E2A\u503C\u7684\u4F4D\u7F6E\u53EF\u4EE5\u91C7\u7528 <code>indexOf</code></li></ol><h2 id="\u56DB\u3001\u4F2A\u5143\u7D20\u6709\u54EA\u4E9B\u4F5C\u7528\u5462\uFF1F" tabindex="-1">\u56DB\u3001\u4F2A\u5143\u7D20\u6709\u54EA\u4E9B\u4F5C\u7528\u5462\uFF1F <a class="header-anchor" href="#\u56DB\u3001\u4F2A\u5143\u7D20\u6709\u54EA\u4E9B\u4F5C\u7528\u5462\uFF1F" aria-hidden="true">#</a></h2><p>\u5728\u8C08\u4F5C\u7528\u4E4B\u524D\uFF0C\u5148\u6765\u533A\u5206\u4E00\u4E0B<strong>\u4F2A\u5143\u7D20\u548C\u4F2A\u7C7B</strong></p><p><strong>\u4F2A\u7C7B</strong>\uFF1A\u4ECE\u5B57\u9762\u4E0A\u6765\u770B\uFF0C\u53EF\u4EE5\u7406\u89E3\u4E3A\u4E00\u4E2A CSS \u7C7B\uFF0C\u5B83\u5C31\u662F\u7528\u6765\u9009\u62E9\u5904\u4E8E\u7279\u5B9A\u72B6\u6001\u7684\u5143\u7D20\u7684\u9009\u62E9\u5668\uFF0C\u6BD4\u5982\u5904\u4E8E <code>hover</code> \u72B6\u6001\u7684\u5143\u7D20\uFF0C\u67D0\u4E2A <code>class</code> \u7684\u7B2C\u51E0\u4E2A\u5143\u7D20\uFF0C\u5B83\u548C\u666E\u901A\u7684\u7C7B\u4E0D\u4E00\u6837\u3002\u6B64\u5916\u4F2A\u7C7B\u4E00\u822C\u662F\u5355\u5192\u53F7\uFF0C\u4F8B\u5982 <code>:hover</code></p><p><strong>\u4F2A\u5143\u7D20</strong>\uFF1A\u4ECE\u5B57\u9762\u4E0A\u770B\uFF0C\u5B83\u662F\u4E00\u4E2A\u5047\u7684\u5143\u7D20\uFF0C\u6211\u4E5F\u662F\u8FD9\u4E48\u7406\u89E3\u7684\uFF0C\u5B83\u7C7B\u4F3C\u6DFB\u52A0\u4E00\u4E2A\u65B0\u7684 <code>DOM</code> \u8282\u70B9\u5230 <code>DOM</code> \u6811\u4E0A\uFF0C\u800C\u4E0D\u662F\u6539\u53D8\u5143\u7D20\u7684\u72B6\u6001\u3002\u4F46\u662F\u8FD9\u91CC\u503C\u5F97\u6CE8\u610F\u7684\u662F\uFF0C\u8FD9\u91CC\u4E0D\u662F\u771F\u7684\u6DFB\u52A0\u4E00\u4E2A\u8282\u70B9\uFF0C\u5B9E\u9645\u4E0A\u8FD9\u4E2A\u5143\u7D20\u88AB\u521B\u5EFA\u5728\u6587\u6863\u4E4B\u5916\u3002</p><p>\u4E3A\u4E86\u4ECE\u5199\u6CD5\u4E0A\u533A\u5206\u4F2A\u7C7B\u548C\u4F4D\u5143\u7D20\uFF0C\u4E00\u822C\u4F2A\u5143\u7D20\u91C7\u7528\u53CC\u5192\u53F7\uFF0C\u4F8B\u5982 <code>::after</code> \uFF0C\u4F46\u662F\u5BF9\u4E8E\u4F2A\u5143\u7D20\u6765\u8BF4\uFF0C\u5355\u5192\u53F7\uFF0C\u53CC\u5192\u53F7\u90FD\u53EF\u4EE5\uFF0C\u5EFA\u8BAE\u89C4\u8303</p><p><strong>\u56DE\u5F52\u6B63\u9898</strong></p><p>\u4F2A\u5143\u7D20\u7684\u4F5C\u7528</p><ol><li>\u4F2A\u5143\u7D20\u80FD\u591F\u51CF\u5C11\u9875\u9762\u4E2D\u7684 <code>DOM</code> \u8282\u70B9\uFF0C\u4F2A\u5143\u7D20\u4E0D\u5C5E\u4E8E HTML \u9875\u9762\uFF0C\u80FD\u591F\u51CF\u5C0F <code>JS</code> \u67E5\u627E <code>DOM</code> \u7684\u8D1F\u62C5\uFF0C\u56E0\u6B64\u53EF\u4EE5\u8BF4\uFF0C\u4F7F\u7528\u4F2A\u5143\u7D20\u80FD\u591F\u4F18\u5316\u6027\u80FD</li><li>\u4F2A\u5143\u7D20\u80FD\u591F\u7528\u6765\u6E05\u9664\u6D6E\u52A8\uFF0C\u7ECF\u5178\u4E09\u4EF6\u5957 <code>content</code> ,<code>display</code> <code>clear</code></li><li>\u52A0\u5FEB\u6D4F\u89C8\u5668\u52A0\u8F7D HTML \u6587\u4EF6</li></ol><h2 id="\u4E94\u3001500-http-\u72B6\u6001\u7801\u662F\u4EC0\u4E48\u5F02\u5E38\uFF1F" tabindex="-1">\u4E94\u3001500 HTTP \u72B6\u6001\u7801\u662F\u4EC0\u4E48\u5F02\u5E38\uFF1F <a class="header-anchor" href="#\u4E94\u3001500-http-\u72B6\u6001\u7801\u662F\u4EC0\u4E48\u5F02\u5E38\uFF1F" aria-hidden="true">#</a></h2><p>500 \u662F<strong>\u670D\u52A1\u5668\u5185\u90E8\u9519\u8BEF</strong></p><p>\u5E38\u89C1\u7684HTTP \u72B6\u6001\u7801</p><table><thead><tr><th><code>HTTP</code> \u72B6\u6001\u7801</th><th style="text-align:center;">means</th></tr></thead><tbody><tr><td>200</td><td style="text-align:center;">\u670D\u52A1\u5668<code>\u6210\u529F\u8FD4\u56DE</code>\u7F51\u9875</td></tr><tr><td>301</td><td style="text-align:center;">\u5BF9\u8C61<code>\u6C38\u4E45</code>\u79FB\u52A8</td></tr><tr><td>302</td><td style="text-align:center;">\u5BF9\u8C61<code>\u4E34\u65F6</code>\u79FB\u52A8</td></tr><tr><td>304</td><td style="text-align:center;">\u8BF7\u6C42\u7684\u7F51\u9875<code>\u672A\u4FEE\u6539</code></td></tr><tr><td>401</td><td style="text-align:center;"><code>\u672A\u6388\u6743</code>\uFF0C\u8BF7\u6C42\u8981\u6C42\u8EAB\u4EFD\u9A8C\u8BC1</td></tr><tr><td>404</td><td style="text-align:center;">\u8BF7\u6C42\u7684<code>\u7F51\u9875\u4E0D\u5B58\u5728</code></td></tr><tr><td>500</td><td style="text-align:center;">\u670D\u52A1\u5668<code>\u5185\u90E8\u9519\u8BEF</code></td></tr><tr><td>503</td><td style="text-align:center;">\u670D\u52A1<code>\u4E0D\u53EF\u7528</code></td></tr></tbody></table><h2 id="\u516D\u3001redux-\u4E2D\u7684-reducer-\u8981\u6C42\u662F\u4E00\u4E2A\u7EAF\u51FD\u6570\u5462\uFF1F" tabindex="-1">\u516D\u3001<code>redux</code> \u4E2D\u7684 <code>reducer</code> \u8981\u6C42\u662F\u4E00\u4E2A\u7EAF\u51FD\u6570\u5462\uFF1F <a class="header-anchor" href="#\u516D\u3001redux-\u4E2D\u7684-reducer-\u8981\u6C42\u662F\u4E00\u4E2A\u7EAF\u51FD\u6570\u5462\uFF1F" aria-hidden="true">#</a></h2><p>\u9996\u5148\u5982\u679C <code>redux</code> \u4E2D\u7684 <code>reducer</code> \u5982\u679C\u4E0D\u662F\u4E00\u4E2A \u7EAF\u51FD\u6570\u7684\u8BDD\u4F1A\u9020\u6210\u4EC0\u4E48\u540E\u679C\u5462\uFF1F</p><p>\u5982\u679C\u6211\u4EEC\u5728 <code>reducer</code> \u4E2D\uFF0C\u5728\u539F\u6765 <code>state</code> \u7684\u57FA\u7840\u4E0A\u8FDB\u884C\u64CD\u4F5C\u7684\u8BDD\uFF0C\u5E76\u4E0D\u4F1A\u8BA9 <code>React</code> \u7EC4\u4EF6\u91CD\u65B0\u6E32\u67D3\uFF0C<strong>\u5E76\u4E0D\u4F1A\u6709\u4EFB\u4F55\u6539\u53D8</strong>\uFF0C\u8FD9\u662F\u7531\u4E8E <code>Redux</code> \u7684\u5E95\u5C42\u5B9E\u73B0\u51B3\u5B9A\u7684</p><p>\u5728\u8FD9\u91CC\u6211\u4EEC\u770B\u770B <code>redux</code> \u7684\u6E90\u7801\uFF0C\u6765\u770B\u770B\u5B83\u5230\u5E95\u662F\u4EC0\u4E48\u539F\u56E0\u9020\u6210\u7684\uFF0C<code>Redux</code> \u63A5\u6536\u4E00\u4E2A <code>state</code> \u5BF9\u8C61\uFF0C\u7136\u540E\u901A\u8FC7 <code>for</code> \u5FAA\u73AF\uFF0C\u5C06 <code>state</code> \u7684\u6BCF\u4E00\u90E8\u5206\u4F20\u9012\u7ED9\u5BF9\u4E8E\u7684 <code>reducer</code> \uFF0C\u5982\u679C\u53D1\u751F\u4EFB\u4F55\u6539\u53D8\uFF0C <code>reducer</code> \u5C06\u8FD4\u56DE\u4E00\u4E2A\u65B0\u7684\u5BF9\u8C61\uFF0C\u4E5F\u5C31\u662F\u8FD9\u91CC\u7684 <code>hasChanged</code> \u5224\u65AD\uFF0C\u6211\u4EEC\u53EF\u4EE5\u53D1\u73B0\uFF0C\u5B83\u53EA\u662F\u901A\u8FC7\u4E86 <code>!==</code> \u6765\u8FDB\u884C\u5224\u65AD\u524D\u540E\u7684 <code>state</code> \u662F\u5426\u76F8\u7B49\uFF0C\u8FD9\u662F\u4E00\u79CD\u6D45\u6BD4\u8F83\u7684\u65B9\u6CD5\uFF0C\u6211\u7684\u7406\u89E3\u5C31\u662F<strong>\u5730\u5740\u6709\u6CA1\u6709\u53D8\u5316</strong></p><p>\u56E0\u6B64\u5982\u679C\u6211\u4EEC\u4F20\u5165\u7684 <code>state</code> \u662F\u5728\u65E7\u7684\u57FA\u7840\u4E0A\u66F4\u6539\u7684\uFF0C\u90A3\u4E48\u5B83\u7684<strong>\u5730\u5740\u662F\u4E0D\u4F1A\u53D1\u751F\u53D8\u5316</strong>\u7684\uFF0C\u56E0\u6B64\u662F\u4E0D\u4F1A\u901A\u8FC7\u8FD9\u5C42\u6D45\u6BD4\u8F83\u7684\uFF0C\u56E0\u6B64 <code>hasChanged</code> \u8FD4\u56DE <code>false</code> \uFF0C<code>state</code> \u6CA1\u6709\u88AB\u66F4\u65B0</p><p>\u90A3\u4E3A\u4EC0\u4E48 <code>redux</code> \u8981\u8FD9\u6837\u8BBE\u8BA1\u5462\uFF1F</p><p>\u5982\u679C\u8FD9\u91CC\u6539\u6210\u6DF1\u6BD4\u8F83\u4E0D\u5C31\u6CA1\u6709\u8FD9\u79CD\u95EE\u9898\u4E86\u5417\uFF1F</p><p>\u539F\u56E0\u662F\uFF0C\u5982\u679C\u8FD9\u91CC\u91C7\u7528\u4E86\u6DF1\u6BD4\u8F83\u7684\u65B9\u5F0F\uFF0C\u5F53\u6BD4\u8F83\u7684\u6B21\u6570\u5F88\u591A\u65F6\uFF0C\u6027\u80FD\u6D88\u8017\u7279\u522B\u5927\uFF0C\u56E0\u6B64 <code>redux</code> \u5BF9 <code>reducer</code> \u505A\u4E86\u4E00\u4E2A\u89C4\u5B9A\uFF0C\u65E0\u8BBA\u53D1\u751F\u4EC0\u4E48\u53D8\u5316\u65F6\uFF0C\u90FD\u9700\u8981\u8FD4\u56DE\u4E00\u4E2A\u65B0\u7684\u5BF9\u8C61\uFF1B\u6CA1\u6709\u53D8\u5316\u65F6\uFF0C\u8FD4\u56DE\u65E7\u7684\u5BF9\u8C61\u3002\u8FD9\u6837\u5C31\u80FD\u7EE7\u7EED\u6CBF\u7528\u6D45\u6BD4\u8F83\u7684\u65B9\u5F0F\uFF0C\u5F88\u597D\u7684\u51CF\u5C11\u4E86\u6027\u80FD\u7684\u635F\u8017</p><p><img src="https://ljcimg.oss-cn-beijing.aliyuncs.com/imgs/image-20211125161432173.png" alt="image-20211125161432173" data-fancybox="gallery"></p><p>\u540C\u65F6\u5728 <code>redux</code> \u7684\u82F1\u6587\u5B98\u7F51\u4E2D\u7ED9 <code>reducers</code> \u5B9A\u5236\u4E86\u4E00\u5957\u89C4\u5219\uFF1A<a href="https://redux.js.org/tutorials/fundamentals/part-3-state-actions-reducers#rules-of-reducers" target="_blank" rel="noopener noreferrer">\u5BF9\u5E94\u5730\u5740</a></p><blockquote><p>We said earlier that <strong>reducers must always follow some special rules</strong>:</p><ul><li>They should only calculate the new state value based on the <code>state</code> and <code>action</code> arguments</li><li>They are not allowed to modify the existing <code>state</code>. Instead, they must make <em>immutable updates</em>, by copying the existing <code>state</code> and making changes to the copied values.</li><li>They must not do any asynchronous logic or other &quot;side effects&quot;</li></ul><p>\u8E69\u811A\u7FFB\u8BD1</p><p>\u6211\u4EEC\u4E4B\u524D\u8BF4\u8FC7\uFF0C<strong>reducer \u5FC5\u987B\u59CB\u7EC8\u9075\u5FAA\u4E00\u4E9B\u7279\u6B8A\u89C4\u5219</strong></p><ul><li>\u5B83\u4EEC\u5E94\u8BE5\u53EA\u6839\u636E <code>state</code> \u548C <code>action</code> \u53C2\u6570\u8BA1\u7B97\u65B0\u7684\u72B6\u6001\u503C</li><li>\u5B83\u4EEC\u4E0D\u5141\u8BB8\u4FEE\u6539\u5F53\u524D\u7684 <code>state</code>\u3002\u76F8\u53CD\uFF0C\u5B83\u4EEC\u5FC5\u987B\u901A\u8FC7\u590D\u5236\u73B0\u5728\u7684 <code>state</code>\uFF0C\u5E76\u5BF9\u590D\u5236\u7684\u503C\u8FDB\u884C\u66F4\u6539\u6765\u8FDB\u884C <code>state</code> \u66F4\u65B0</li><li>\u5B83\u4EEC\u4E0D\u80FD\u505A\u4EFB\u4F55\u5F02\u6B65\u903B\u8F91\u4EE5\u53CA\u5176\u4ED6\u201D\u526F\u4F5C\u7528\u201C</li></ul></blockquote><p>\u9075\u5FAA\u8FD9\u4E9B\u89C4\u5219\u7684\u51FD\u6570\u4E5F\u88AB\u79F0\u4E3A**\u201C\u7EAF\u201D\u51FD\u6570**\uFF0C\u56E0\u6B64 <code>reducer</code> \u9700\u8981\u4E00\u4E2A\u7EAF\u51FD\u6570\u7531\u6B64\u800C\u6765</p><p>\u{1F449} \u603B\u7ED3\u4EE5\u4E0B</p><ol><li><code>redux</code> \u5E95\u5C42\u91C7\u7528\u4E86\u6D45\u6BD4\u8F83\u7684\u65B9\u5F0F\u6765\u5224\u65AD <code>state</code> \u6539\u53D8\uFF0C\u6765\u4F18\u5316\u6027\u80FD</li><li>\u91C7\u7528\u7EAF\u51FD\u6570\uFF0C\u4FDD\u8BC1\u65B0\u65E7 <code>state</code> \u4E0D\u662F\u540C\u4E00\u4E2A\u5BF9\u8C61\u5F15\u7528</li><li>\u4E3A\u4E86\u4FDD\u8BC1\u8FD4\u56DE\u65B0\u7684 <code>state</code> \u662F\u786E\u5B9A\u7684\uFF0C\u4E0D\u4F1A\u56E0\u4E3A\u526F\u4F5C\u7528\u8FD4\u56DE\u4E0D\u786E\u5B9A\u7684 <code>state</code></li></ol><hr><h2 id="\u{1F4D6}-\u603B\u7ED3" tabindex="-1">\u{1F4D6} \u603B\u7ED3 <a class="header-anchor" href="#\u{1F4D6}-\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u901A\u8FC7\u8FD9\u51E0\u9053\u9762\u8BD5\u9898\uFF0C\u6211\u4EEC\u590D\u4E60\u4E86 <code>JavaScript</code> \u4E2D\u7684\u5FAA\u73AF\u8BED\u53E5\uFF0C\u4E5F\u533A\u5206\u4E86 <code>splice</code> \u548C <code>slice</code> \u800C\u53C8\u6DF1\u5165\u7406\u89E3 <code>redux</code> \u4E2D <code>reducer</code> \u7684\u5DE5\u4F5C\u539F\u7406\uFF0C\u8FD9\u5BF9\u6211\u81EA\u5DF1\u6765\u8BF4\u63D0\u5347\u8FD8\u662F\u5F88\u5927\u7684\uFF0C\u4E0D\u77E5\u9053\u770B\u5230\u8FD9\u91CC\u7684\u4F60\u6709\u6CA1\u6709\u4EC0\u4E48\u6536\u83B7\u5462\uFF1F\u53EF\u80FD\u6587\u4E2D\u6709\u7406\u89E3\u4E0D\u5230\u4F4D\u7684\u5730\u65B9\uFF0C\u6B22\u8FCE\u5728\u8BC4\u8BBA\u533A\u6307\u51FA\uFF0C\u6211\u4EEC\u4E00\u8D77\u8FDB\u6B65\uFF0C\u5171\u540C\u6210\u957F\uFF01</p><p>\u6700\u540E\uFF0C\u6211\u662F\u5C0F\u4E1E\u540C\u5B66\uFF0C\u6B22\u8FCE\u5927\u5BB6\u5173\u6CE8\u672C\u4E13\u680F\uFF0C\u6301\u7EED\u5173\u6CE8\u6700\u65B0\u6587\u7AE0~\u795D\u613F\u5927\u5BB6\u62FF\u5230\u5FC3\u4EEA\u7684 <code>offer</code></p><blockquote><p>\u6700\u540E\uFF0C\u53EF\u80FD\u5728\u5F88\u591A\u5730\u65B9\u8BB2\u8BC9\u7684\u4E0D\u591F\u6E05\u6670\uFF0C\u8BF7\u89C1\u8C05</p><p>\u{1F48C} \u5982\u679C\u6587\u7AE0\u6709\u4EC0\u4E48\u9519\u8BEF\u7684\u5730\u65B9\uFF0C\u6216\u8005\u6709\u4EC0\u4E48\u7591\u95EE\uFF0C\u6B22\u8FCE\u7559\u8A00\uFF0C\u4E5F\u6B22\u8FCE\u79C1\u4FE1\u4EA4\u6D41</p></blockquote>',74),r=[i];function a(l,s,n,p,g,u){return d(),o("div",null,r)}var m=e(t,[["render",a]]);export{h as __pageData,m as default};
