import{_ as e,c as t,o as a,a as o}from"./app.ae2cfb91.js";const _=JSON.parse('{"title":"Q & A","description":"","frontmatter":{},"headers":[{"level":2,"title":"startTransition \u76F8\u6BD4 setTimeout \u7684\u4F18\u52BF\u548C\u5F02\u540C\u662F\u4EC0\u4E48\uFF1F","slug":"starttransition-\u76F8\u6BD4-settimeout-\u7684\u4F18\u52BF\u548C\u5F02\u540C\u662F\u4EC0\u4E48\uFF1F"},{"level":2,"title":"useLayoutEffect \u4E0E useEffect \u7684\u533A\u522B\u662F\u4EC0\u4E48\uFF1F","slug":"uselayouteffect-\u4E0E-useeffect-\u7684\u533A\u522B\u662F\u4EC0\u4E48\uFF1F"},{"level":2,"title":"\uFF08\u8FFD\u95EE\uFF09\u4EC0\u4E48\u60C5\u51B5\u4E0B\u4F7F\u7528 useLayoutEffect\uFF1F","slug":"\uFF08\u8FFD\u95EE\uFF09\u4EC0\u4E48\u60C5\u51B5\u4E0B\u4F7F\u7528-uselayouteffect\uFF1F"}],"relativePath":"pages/react/hard/hooks/qa.md"}'),s={name:"pages/react/hard/hooks/qa.md"},i=o('<h1 id="q-a" tabindex="-1">Q &amp; A <a class="header-anchor" href="#q-a" aria-hidden="true">#</a></h1><h2 id="starttransition-\u76F8\u6BD4-settimeout-\u7684\u4F18\u52BF\u548C\u5F02\u540C\u662F\u4EC0\u4E48\uFF1F" tabindex="-1">startTransition \u76F8\u6BD4 setTimeout \u7684\u4F18\u52BF\u548C\u5F02\u540C\u662F\u4EC0\u4E48\uFF1F <a class="header-anchor" href="#starttransition-\u76F8\u6BD4-settimeout-\u7684\u4F18\u52BF\u548C\u5F02\u540C\u662F\u4EC0\u4E48\uFF1F" aria-hidden="true">#</a></h2><ul><li><p>\u4E00\u65B9\u9762\uFF1A<code>startTransition</code> \u7684\u5904\u7406\u903B\u8F91\u548C setTimeout \u6709\u4E00\u4E2A\u5F88\u91CD\u8981\u7684\u533A\u522B\uFF0CsetTimeout \u662F\u5F02\u6B65\u5EF6\u65F6\u6267\u884C\uFF0C\u800C <code>startTransition</code> \u7684\u56DE\u8C03\u51FD\u6570\u662F\u540C\u6B65\u6267\u884C\u7684\u3002 \u5728 <code>startTransition</code> \u4E4B\u4E2D\u4EFB\u4F55\u66F4\u65B0\uFF0C\u90FD\u4F1A\u6807\u8BB0\u4E0A transition\uFF0CReact \u5C06\u5728\u66F4\u65B0\u7684\u65F6\u5019\uFF0C\u5224\u65AD\u8FD9\u4E2A\u6807\u8BB0\u6765\u51B3\u5B9A\u662F\u5426\u5B8C\u6210\u6B64\u6B21\u66F4\u65B0\u3002\u6240\u4EE5 Transition \u53EF\u4EE5\u7406\u89E3\u6210\u6BD4 setTimeout \u66F4\u65E9\u7684\u66F4\u65B0\u3002\u4F46\u662F\u540C\u65F6\u8981\u4FDD\u8BC1 ui \u7684\u6B63\u5E38\u54CD\u5E94\uFF0C\u5728\u6027\u80FD\u597D\u7684\u8BBE\u5907\u4E0A\uFF0Ctransition \u4E24\u6B21\u66F4\u65B0\u7684\u5EF6\u8FDF\u4F1A\u5F88\u5C0F\uFF0C\u4F46\u662F\u5728\u6162\u7684\u8BBE\u5907\u4E0A\uFF0C\u5EF6\u65F6\u4F1A\u5F88\u5927\uFF0C\u4F46\u662F\u4E0D\u4F1A\u5F71\u54CD UI \u7684\u54CD\u5E94\u3002</p></li><li><p>\u53E6\u4E00\u65B9\u9762\uFF0C\u5C31\u662F\u901A\u8FC7\u4E0A\u9762\u4F8B\u5B50\uFF0C\u53EF\u4EE5\u770B\u5230\uFF0C\u5BF9\u4E8E\u6E32\u67D3\u5E76\u53D1\u7684\u573A\u666F\u4E0B\uFF0C<code>setTimeout</code> \u4ECD\u7136\u4F1A\u4F7F\u9875\u9762\u5361\u987F\u3002\u56E0\u4E3A\u8D85\u65F6\u540E\uFF0C\u8FD8\u4F1A\u6267\u884C setTimeout \u7684\u4EFB\u52A1\uFF0C\u5B83\u4EEC\u4E0E\u7528\u6237\u4EA4\u4E92\u540C\u6837\u5C5E\u4E8E\u5B8F\u4EFB\u52A1\uFF0C\u6240\u4EE5\u4ECD\u7136\u4F1A\u963B\u6B62\u9875\u9762\u7684\u4EA4\u4E92\u3002 \u90A3\u4E48 transition \u5C31\u4E0D\u540C\u4E86\uFF0C\u5728 conCurrent mode \u4E0B\uFF0C<code>startTransition</code> \u662F\u53EF\u4EE5\u4E2D\u65AD\u6E32\u67D3\u7684 \uFF0C\u6240\u4EE5\u5B83\u4E0D\u4F1A\u8BA9\u9875\u9762\u5361\u987F\uFF0CReact \u8BA9\u8FD9\u4E9B\u4EFB\u52A1\uFF0C\u5728\u6D4F\u89C8\u5668\u7A7A\u95F2\u65F6\u95F4\u6267\u884C\uFF0C\u6240\u4EE5\u4E0A\u8FF0\u8F93\u5165 input \u5185\u5BB9\u65F6\uFF0C<code>startTransition</code> \u4F1A\u4F18\u5148\u5904\u7406 input \u503C\u7684\u66F4\u65B0\uFF0C\u800C\u4E4B\u540E\u624D\u662F\u5217\u8868\u7684\u6E32\u67D3</p></li></ul><h2 id="uselayouteffect-\u4E0E-useeffect-\u7684\u533A\u522B\u662F\u4EC0\u4E48\uFF1F" tabindex="-1">useLayoutEffect \u4E0E useEffect \u7684\u533A\u522B\u662F\u4EC0\u4E48\uFF1F <a class="header-anchor" href="#uselayouteffect-\u4E0E-useeffect-\u7684\u533A\u522B\u662F\u4EC0\u4E48\uFF1F" aria-hidden="true">#</a></h2><p><code>useLayoutEffect</code>\uFF1A\u8FD9\u4E2A\u94A9\u5B50\u51FD\u6570\u4F1A\u5728 <strong>DOM \u66F4\u65B0\u4E4B\u524D\u540C\u6B65\u5730\u6267\u884C</strong>\u3002\u5B83\u4F1A\u5728\u7EC4\u4EF6\u6E32\u67D3\u5B8C\u6BD5\u540E\u3001DOM \u66F4\u65B0\u4E4B\u524D\u88AB\u8C03\u7528\uFF0C\u56E0\u6B64\u5B83\u7684\u6267\u884C\u4F1A<strong>\u963B\u585E\u9875\u9762\u6E32\u67D3</strong>\u3002\u8FD9\u4E2A\u94A9\u5B50\u51FD\u6570\u63D0\u4F9B\u4E86\u4E00\u79CD\u5728\u6E32\u67D3\u4E4B\u524D\u540C\u6B65\u66F4\u65B0 DOM \u7684\u65B9\u6CD5\uFF0C\u53EF\u4EE5\u7528\u4E8E\u5B9E\u73B0\u4E00\u4E9B\u9700\u8981\u8BA1\u7B97\u5E03\u5C40\u7684\u64CD\u4F5C\uFF0C\u4F8B\u5982\u6D4B\u91CF DOM \u5C3A\u5BF8\u3001\u66F4\u65B0\u6EDA\u52A8\u4F4D\u7F6E\u7B49\u3002</p><p><code>useEffect</code>\uFF1A\u8FD9\u4E2A\u94A9\u5B50\u51FD\u6570\u4F1A\u5728\u7EC4\u4EF6\u6E32\u67D3\u5B8C\u6BD5\u540E\u5F02\u6B65\u5730\u6267\u884C\u3002\u5B83\u4F1A\u5728 DOM \u66F4\u65B0\u4E4B\u540E\u88AB\u8C03\u7528\uFF0C\u56E0\u6B64\u5B83\u4E0D\u4F1A\u963B\u585E\u9875\u9762\u6E32\u67D3\u3002\u5B83\u901A\u5E38\u7528\u4E8E\u5904\u7406\u4E00\u4E9B\u4E0D\u9700\u8981\u540C\u6B65\u66F4\u65B0 DOM \u7684\u526F\u4F5C\u7528\uFF0C\u4F8B\u5982\u6570\u636E\u83B7\u53D6\u3001\u8BA2\u9605\u4E8B\u4EF6\u7B49\u3002\u7531\u4E8E\u5B83\u662F\u5F02\u6B65\u6267\u884C\u7684\uFF0C\u56E0\u6B64\u5B83\u5BF9\u9875\u9762\u6027\u80FD\u7684\u5F71\u54CD\u8F83\u5C0F\u3002</p><p>\u4E24\u8005\u90FD\u80FD\u83B7\u53D6\u5230\u6700\u65B0\u7684 DOM \u6570\u636E\uFF0C\u4F46\u662F <code>useLayoutEffect</code> \u4F1A\u5728\u6D4F\u89C8\u5668\u7ED8\u5236\u4E4B\u524D\u6267\u884C\uFF0C\u56E0\u6B64\u4F1A\u963B\u585E\u6D4F\u89C8\u5668\u7684\u6E32\u67D3\uFF0C\u6240\u4EE5\u4F1A\u5F71\u54CD\u9875\u9762\u7684\u6027\u80FD\u3002\u800C <code>useEffect</code> \u5219\u4E0D\u4F1A\u963B\u585E\u6D4F\u89C8\u5668\u7684\u6E32\u67D3\uFF0C\u6240\u4EE5\u4E0D\u4F1A\u5F71\u54CD\u9875\u9762\u7684\u6027\u80FD\u3002</p><h2 id="\uFF08\u8FFD\u95EE\uFF09\u4EC0\u4E48\u60C5\u51B5\u4E0B\u4F7F\u7528-uselayouteffect\uFF1F" tabindex="-1">\uFF08\u8FFD\u95EE\uFF09\u4EC0\u4E48\u60C5\u51B5\u4E0B\u4F7F\u7528 useLayoutEffect\uFF1F <a class="header-anchor" href="#\uFF08\u8FFD\u95EE\uFF09\u4EC0\u4E48\u60C5\u51B5\u4E0B\u4F7F\u7528-uselayouteffect\uFF1F" aria-hidden="true">#</a></h2><ol><li>\u6D4B\u91CF DOM \u5C3A\u5BF8</li></ol><p><code>useLayoutEffect</code> \u53EF\u4EE5\u7528\u4E8E\u8BA1\u7B97 DOM \u5143\u7D20\u7684\u5C3A\u5BF8\u3002\u5728\u7EC4\u4EF6\u6E32\u67D3\u524D\u6D4B\u91CF DOM \u5143\u7D20\u7684\u5927\u5C0F\u53EF\u4EE5\u4FDD\u8BC1\u540E\u7EED\u7684\u8BA1\u7B97\u548C\u5E03\u5C40\u80FD\u591F\u6B63\u786E\u5730\u8FDB\u884C\uFF0C<strong>\u907F\u514D\u7531\u4E8E\u8BA1\u7B97\u5EF6\u65F6\u5BFC\u81F4\u7684\u9875\u9762\u6296\u52A8\u95EE\u9898\u3002</strong></p><ol start="2"><li><p>\u66F4\u65B0\u6EDA\u52A8\u4F4D\u7F6E \u5982\u679C\u9700\u8981\u540C\u6B65\u91CD\u65B0\u8BA1\u7B97\u6EDA\u52A8\u4F4D\u7F6E\uFF0C\u53EF\u4EE5\u4F7F\u7528 <code>useLayoutEffect</code> \u5B9E\u73B0\u3002\u5728\u7EC4\u4EF6\u6E32\u67D3\u5B8C\u6210\u540E\uFF0C\u8FD9\u4E2A\u94A9\u5B50\u51FD\u6570\u53EF\u4EE5\u786E\u4FDD\u6B63\u786E\u8BA1\u7B97\u548C\u66F4\u65B0\u6EDA\u52A8\u4F4D\u7F6E\u3002</p></li><li><p>\u66F4\u65B0 DOM \u6837\u5F0F \u4F7F\u7528 <code>useLayoutEffect</code> \u53EF\u4EE5\u540C\u6B65\u66F4\u65B0 DOM \u6837\u5F0F\uFF0C\u4EE5\u786E\u4FDD\u6B63\u786E\u8BA1\u7B97\u5E03\u5C40\u3002\u5728\u7EC4\u4EF6\u6E32\u67D3\u5B8C\u6210\u4E4B\u540E\u7ACB\u5373\u66F4\u65B0\u6837\u5F0F\u53EF\u4EE5<strong>\u9632\u6B62\u53EF\u80FD\u5B58\u5728\u7684\u6837\u5F0F\u95EA\u70C1\u95EE\u9898\u3002</strong></p></li></ol>',11),c=[i];function r(n,u,d,f,l,h){return a(),t("div",null,c)}var T=e(s,[["render",r]]);export{_ as __pageData,T as default};
