import{_ as e,c as o,o as d,a as r}from"./app.257fad07.js";var t="/assets/1.103fe364.png",c="/assets/2.cad6657e.png";const _=JSON.parse('{"title":"\u72B6\u6001\u66F4\u65B0\u6D41\u7A0B\u6982\u89C8","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u524D\u8A00","slug":"\u524D\u8A00"},{"level":2,"title":"\u6700\u5C0F\u66F4\u65B0\u5355\u5143","slug":"\u6700\u5C0F\u66F4\u65B0\u5355\u5143"},{"level":2,"title":"\u89E6\u53D1\u66F4\u65B0","slug":"\u89E6\u53D1\u66F4\u65B0"},{"level":2,"title":"\u521B\u5EFA Update \u5BF9\u8C61","slug":"\u521B\u5EFA-update-\u5BF9\u8C61"},{"level":2,"title":"\u6807\u8BB0 RootFiber","slug":"\u6807\u8BB0-rootfiber"},{"level":2,"title":"\u8C03\u5EA6\u66F4\u65B0","slug":"\u8C03\u5EA6\u66F4\u65B0"},{"level":2,"title":"\u603B\u7ED3","slug":"\u603B\u7ED3"}],"relativePath":"pages/react/hard/update.md"}'),a={name:"pages/react/hard/update.md"},i=r('<h1 id="\u72B6\u6001\u66F4\u65B0\u6D41\u7A0B\u6982\u89C8" tabindex="-1">\u72B6\u6001\u66F4\u65B0\u6D41\u7A0B\u6982\u89C8 <a class="header-anchor" href="#\u72B6\u6001\u66F4\u65B0\u6D41\u7A0B\u6982\u89C8" aria-hidden="true">#</a></h1><h2 id="\u524D\u8A00" tabindex="-1">\u524D\u8A00 <a class="header-anchor" href="#\u524D\u8A00" aria-hidden="true">#</a></h2><p>\u524D\u9762\u6211\u4EEC\u8BB2\u4E86 <code>render</code> \u9636\u6BB5\u548C <code>commit</code> \u9636\u6BB5\u7684\u5DE5\u4F5C\uFF0C\u8BB2\u4E86 <code>render</code> \u9636\u6BB5\u4E2D Diff \u7B97\u6CD5\u7684\u5B9E\u73B0\uFF0C\u73B0\u5728\u6211\u4EEC\u5DF2\u7ECF\u5BF9\u6574\u4F53\u7684\u6D41\u7A0B\u6709\u4E86\u4E00\u5B9A\u7684\u8BA4\u8BC6</p><ul><li>\u5728 <code>render</code> \u9636\u6BB5\u7684 <code>beginWork</code> \u9636\u6BB5\u4F1A\u521B\u5EFA\u5B50 <code>Fiber</code> \u8282\u70B9\uFF0C\u901A\u8FC7 Diff \u7B97\u6CD5\uFF0C\u6253\u4E0A\u76F8\u5E94\u7684 <code>effectTag</code></li><li>\u5728 <code>render</code> \u9636\u6BB5\u7684 <code>completeWork</code> \u9636\u6BB5\u4F1A\u6839\u636E <code>Fiber</code> \u8282\u70B9\u751F\u6210\u5BF9\u5E94\u7684 <code>DOM</code> \u8282\u70B9\uFF0C\u5E76\u8FDE\u63A5\u5B50\u8282\u70B9</li><li>\u5728 <code>commit</code> \u9636\u6BB5\u4F1A\u5BF9\u6240\u6709\u7684 <code>effectTag</code> \u6267\u884C\u76F8\u5E94\u7684 DOM \u64CD\u4F5C\uFF0C\u66F4\u65B0\u89C6\u56FE</li></ul><p>\u5728\u8FD9\u4E9B\u6D41\u7A0B\u4E4B\u524D\u8FD8\u6709\u7740\uFF0C\u4ECE<strong>\u89E6\u53D1\u72B6\u6001\u66F4\u65B0\u5230 <code>render</code> \u9636\u6BB5</strong>\u7684\u8FC7\u7A0B\uFF0C\u4E5F\u5C31\u662F\u6211\u4EEC\u672C\u7AE0\u9700\u8981\u5B66\u4E60\u7684\u5185\u5BB9\uFF1A<strong>\u72B6\u6001\u66F4\u65B0</strong></p><p>\u9996\u5148\u6211\u4EEC\u5148\u4E86\u89E3\u4E00\u4E0B\u4EC0\u4E48\u662F<strong>\u6700\u5C0F\u66F4\u65B0\u5355\u5143</strong></p><h2 id="\u6700\u5C0F\u66F4\u65B0\u5355\u5143" tabindex="-1">\u6700\u5C0F\u66F4\u65B0\u5355\u5143 <a class="header-anchor" href="#\u6700\u5C0F\u66F4\u65B0\u5355\u5143" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u4ECE\u5B9E\u4F8B\u6765\u770B\uFF0C\u5BF9\u4E8E\u4E00\u4E2A\u7EC4\u4EF6\u6765\u8BF4\uFF0C\u5982\u679C\u60F3\u8981\u89E6\u53D1\u66F4\u65B0\uFF0C\u90A3\u4E48\u53EF\u4EE5\u6709\u4EE5\u4E0B\u8FD9\u4E9B\u60C5\u51B5</p><ul><li>\u7EC4\u4EF6\u672C\u8EAB\u7684 <code>state</code> \u53D1\u751F\u6539\u53D8</li><li>\u7EC4\u4EF6 <code>props</code> \u7684\u6539\u53D8\uFF0C\u4E5F\u5C31\u662F\u7236\u7EC4\u4EF6\u72B6\u6001\u5BFC\u81F4\u5B50\u7EC4\u4EF6\u66F4\u65B0</li><li><code>context</code> \u6539\u53D8\uFF0C\u8BE5\u7EC4\u4EF6\u6D88\u8D39\u7684 <code>context</code> \u53D1\u751F\u4E86\u66F4\u65B0</li></ul><p>\u4F46\u662F\u5F52\u6839\u7ED3\u5E95\uFF0C\u65E0\u8BBA\u662F\u54EA\u79CD\u573A\u666F\u4E0B\u89E6\u53D1\u7684\u66F4\u65B0\uFF0C\u6700\u7EC8\u7684\u672C\u8D28\u90FD\u662F <code>state</code> \u7684\u53D8\u5316\u5BFC\u81F4\u7684<br> \u5BF9\u4E8E React \u6765\u8BF4\uFF0C\u80FD\u591F\u89E6\u53D1 state \u66F4\u65B0\u7684\u57FA\u672C\u90FD\u662F<strong>\u5728\u7EC4\u4EF6\u5C42\u9762</strong>\u4E0A\uFF0C\u6BD5\u7ADF Fiber \u6CA1\u6709\u529E\u6CD5\u5B9E\u73B0\u81EA\u6211\u66F4\u65B0\uFF0C\u53EA\u80FD\u4F9D\u8D56\u7EC4\u4EF6\u8FDB\u884C <code>state</code> \u66F4\u60F3\u4F60<br> \u56E0\u6B64\uFF0C\u6211\u4EEC\u53EF\u4EE5\u8BA4\u4E3A <strong>\u6700\u5C0F\u7684\u66F4\u65B0\u5355\u5143\u662F\u7EC4\u4EF6\uFF0C\u66F4\u65B0\u6E90\u81EA\u4E8E state \u7684\u53D8\u5316</strong></p><p><strong>\u90A3\u4E48\u5982\u4F55\u89E6\u53D1\u66F4\u65B0\u5462\uFF1F</strong></p><h2 id="\u89E6\u53D1\u66F4\u65B0" tabindex="-1">\u89E6\u53D1\u66F4\u65B0 <a class="header-anchor" href="#\u89E6\u53D1\u66F4\u65B0" aria-hidden="true">#</a></h2><p>\u7ADF\u7136\u7EC4\u4EF6\u662F\u6700\u5C0F\u7684\u66F4\u65B0\u5355\u5143\uFF0C\u90A3\u4E48\u6211\u4EEC\u53EF\u4EE5\u77E5\u9053\uFF0C\u89E6\u53D1\u66F4\u65B0\u53EF\u4EE5\u5206\u4E3A</p><ul><li>\u7C7B\u7EC4\u4EF6\u7684 <code>setState</code> \u72B6\u6001\u66F4\u65B0</li><li>\u51FD\u6570\u7EC4\u4EF6\u7684 <code>useState</code> \u72B6\u6001\u66F4\u65B0</li></ul><p><strong>\u90A3\u4E48\uFF0C\u72B6\u6001\u66F4\u65B0\u90FD\u4F1A\u89E6\u53D1\u54EA\u4E9B\u6D41\u7A0B\u5462\uFF1F</strong></p><h2 id="\u521B\u5EFA-update-\u5BF9\u8C61" tabindex="-1">\u521B\u5EFA Update \u5BF9\u8C61 <a class="header-anchor" href="#\u521B\u5EFA-update-\u5BF9\u8C61" aria-hidden="true">#</a></h2><p>\u9996\u5148\uFF0C\u6BCF\u6B21\u72B6\u6001\u66F4\u65B0\u90FD\u4F1A\u521B\u5EFA\u4E00\u4E2A\u4FDD\u5B58\u66F4\u65B0\u72B6\u6001\u5185\u5BB9\u7684\u5BF9\u8C61\uFF0C\u4E5F\u5C31\u662F Update \u5BF9\u8C61\uFF0C\u4FDD\u5B58\u5728 updateQueue \u94FE\u8868\u4E2D\uFF0C\u8BB0\u5F55\u5F53\u524D Fiber \u8282\u70B9\u6536\u96C6\u5230\u7684\u66F4\u65B0\u3002\u5728 <code>render</code> \u9636\u6BB5\u7684 <code>beginWork</code> \u4E2D\u4F1A\u6839\u636E <code>Update</code> \u5BF9\u8C61\u6765\u8BA1\u7B97\u65B0\u7684 <code>state</code><br><img src="'+t+'" alt="image.png">)<br>\u8FD9\u4E2A Update \u4F1A\u88AB\u4FDD\u5B58\u5728\u4E00\u4E2A\u73AF\u72B6\u94FE\u8868\u4E2D\uFF0C\u63A5\u4E0B\u6765\u4F1A\u8C03\u7528 <code>scheduleUpdateOnFiber</code> \u65B9\u6CD5\uFF0C\u6765\u8C03\u5EA6\u8FD9\u4E2A Update</p><h2 id="\u6807\u8BB0-rootfiber" tabindex="-1">\u6807\u8BB0 RootFiber <a class="header-anchor" href="#\u6807\u8BB0-rootfiber" aria-hidden="true">#</a></h2><p>\u6211\u4EEC\u77E5\u9053 <code>render</code> \u9636\u6BB5\u662F\u4ECE <code>rootFiber</code> \u5F00\u59CB\u5411\u4E0B\u904D\u5386\uFF0C\u56E0\u6B64\u6211\u4EEC\u9700\u8981\u5728 <code>render</code> \u9636\u6BB5\u5F00\u59CB\u4E4B\u524D\uFF0C\u8BA9 <code>rootFiber</code> \u77E5\u9053\u672C\u6B21\u8C03\u5EA6\u7684\u76F8\u5173\u4FE1\u606F\u3002<br>\u56E0\u6B64\u6211\u4EEC\u9700\u8981\u4ECE\u5F53\u524D\u89E6\u53D1\u66F4\u65B0\u7684\u8282\u70B9\u5BF9\u5E94\u7684 Fiber <strong>\u4E00\u76F4\u904D\u5386\u5230\u5E94\u7528\u7684\u6839\u8282\u70B9 rootFiber</strong>\uFF0C\u5E76\u901A\u77E5\u6CBF\u9014\u7684 Fiber\uFF0C\u4F60\u6709\u5B50\u5B59\u8282\u70B9\u88AB\u66F4\u65B0\u4E86\uFF0C\u6253\u4E0A<strong>\u672C\u6B21\u89E6\u53D1\u66F4\u65B0\u7684\u4F18\u5148\u7EA7\u6807\u8BB0</strong>\uFF0C\u5E76\u8FD4\u56DE rootFiber<br>\u8FD9\u4E00\u6B65\u53EB\u505A <code>markUpdateLaneFromFiberToRoot</code></p><h2 id="\u8C03\u5EA6\u66F4\u65B0" tabindex="-1">\u8C03\u5EA6\u66F4\u65B0 <a class="header-anchor" href="#\u8C03\u5EA6\u66F4\u65B0" aria-hidden="true">#</a></h2><p>\u7ECF\u8FC7\u4E86\u4E0A\u9762\u7684\u5904\u7406\uFF0C\u6211\u4EEC\u5DF2\u7ECF\u83B7\u53D6\u5230\u4E86 <code>RootFiber</code>\uFF0C\u5E76\u4E14\u5728\u8FD9\u4E2A Fiber \u4E0A\u540C\u65F6\u6807\u8BB0\u4E86\u5B50\u6811\u4E2D\u7684 Update<br>\u63A5\u4E0B\u6765\uFF0C\u9700\u8981\u901A\u77E5 Scheduler \u8C03\u5EA6\u5668\u6765\u6839\u636E\u66F4\u65B0\u7684\u4F18\u5148\u7EA7\uFF0C\u4EE5\u53CA\u4EFB\u52A1\u7684\u7C7B\u578B\uFF0C\u6765\u53D1\u8D77\u5F02\u6B65\u6216\u662F\u540C\u6B65\u7684\u66F4\u65B0\u8C03\u5EA6\uFF0C\u8FD9\u4E00\u6B65\u8C03\u7528\u7684\u662F <code>ensureRootIsScheduled</code> \u65B9\u6CD5</p><ul><li>\u5BF9\u4E8E legacy \u6A21\u5F0F\u4E0B\uFF0C\u6700\u540E\u6267\u884C\u7684\u662F <code>performSyncWorkOnRoot</code> \u65B9\u6CD5</li><li>\u5BF9\u4E8E concurrent \u6A21\u5F0F\uFF0C\u6700\u540E\u6267\u884C\u7684\u662F <code>performConcurrentWorkOnRoot</code> \u65B9\u6CD5</li></ul><h2 id="\u603B\u7ED3" tabindex="-1">\u603B\u7ED3 <a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a></h2><p>\u5F53\u8C03\u5EA6\u7684\u56DE\u8C03\u51FD\u6570\u88AB\u6267\u884C\uFF0C\u4F1A\u8FDB\u5165\u7EC4\u4EF6\u7684 <code>render</code> \u9636\u6BB5</p><ul><li>\u5728 <code>render</code> \u9636\u6BB5\u7684 <code>reconcile</code> \u4E5F\u5C31\u662F diff \u7B97\u6CD5\u4E2D\uFF0C\u4F1A\u6839\u636E <code>Update</code> \u5BF9\u8C61\uFF0C\u8FD4\u56DE\u5BF9\u5E94\u7684 <code>state</code>\uFF0C\u6839\u636E <code>state</code> \u5224\u65AD\u672C\u6B21\u662F\u5426\u9700\u8981\u66F4\u65B0\u89C6\u56FE\uFF0C\u5982\u679C\u9700\u8981\u66F4\u65B0\u89C6\u56FE\u5C31\u4F1A\u88AB\u6807\u8BB0\u4E3A <code>effectTag</code></li><li>\u5728 <code>commit</code> \u9636\u6BB5\uFF0C\u6807\u8BB0\u4E86 <code>effectTag</code> \u7684 <code>Fiber</code>\uFF0C\u5C31\u4F1A\u6267\u884C\u5BF9\u5E94\u7684\u89C6\u56FE\u66F4\u65B0</li></ul><p><img src="'+c+'" alt="image.png"></p>',26),n=[i];function s(l,p,h,u,g,b){return d(),o("div",null,n)}var m=e(a,[["render",s]]);export{_ as __pageData,m as default};
